INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Ab Crunch Machine', 'Abdominals', 'Machine', 'Abdominals', array[]::text[], array['Select a light resistance and sit down on the ab machine placing your feet under the pads provided and grabbing the top handles. Your arms should be bent at a 90 degree angle as you rest the triceps on the pads provided. This will be your starting position.', 'At the same time, begin to lift the legs up as you crunch your upper torso. Breathe out as you perform this movement. Tip: Be sure to use a slow and controlled motion. Concentrate on using your abs to move the weight while relaxing your legs and feet.', 'After a second pause, slowly return to the starting position as you breathe in.', 'Repeat the movement for the prescribed amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Ab Roller', 'Abdominals', 'Other', 'Abdominals', array['Shoulders'], array['Hold the Ab Roller with both hands and kneel on the floor.', 'Now place the ab roller on the floor in front of you so that you are on all your hands and knees (as in a kneeling push up position). This will be your starting position.', 'Slowly roll the ab roller straight forward, stretching your body into a straight position. Tip: Go down as far as you can without touching the floor with your body. Breathe in during this portion of the movement.', 'After a pause at the stretched position, start pulling yourself back to the starting position as you breathe out. Tip: Go slowly and keep your abs tight at all times.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Air Bike', 'Abdominals', 'Bodyweight', 'Abdominals', array[]::text[], array['Lie flat on the floor with your lower back pressed to the ground. For this exercise, you will need to put your hands beside your head. Be careful however to not strain with the neck as you perform it. Now lift your shoulders into the crunch position.', 'Bring knees up to where they are perpendicular to the floor, with your lower legs parallel to the floor. This will be your starting position.', 'Now simultaneously, slowly go through a cycle pedal motion kicking forward with the right leg and bringing in the knee of the left leg. Bring your right elbow close to your left knee by crunching to the side, as you breathe out.', 'Go back to the initial position as you breathe in.', 'Crunch to the opposite side as you cycle your legs and bring closer your left elbow to your right knee and exhale.', 'Continue alternating in this manner until all of the recommended repetitions for each side have been completed.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Alternate Hammer Curl', 'Biceps', 'Dumbbell', 'Biceps', array['Forearms'], array['Stand up with your torso upright and a dumbbell in each hand being held at arms length. The elbows should be close to the torso.', 'The palms of the hands should be facing your torso. This will be your starting position.', 'While holding the upper arm stationary, curl the right weight forward while contracting the biceps as you breathe out. Continue the movement until your biceps is fully contracted and the dumbbells are at shoulder level. Hold the contracted position for a second as you squeeze the biceps. Tip: Only the forearms should move.', 'Slowly begin to bring the dumbbells back to starting position as your breathe in.', 'Repeat the movement with the left hand. This equals one repetition.', 'Continue alternating in this manner for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Alternate Heel Touchers', 'Abdominals', 'Bodyweight', 'Abdominals', array[]::text[], array['Lie on the floor with the knees bent and the feet on the floor around 18-24 inches apart. Your arms should be extended by your side. This will be your starting position.', 'Crunch over your torso forward and up about 3-4 inches to the right side and touch your right heel as you hold the contraction for a second. Exhale while performing this movement.', 'Now go back slowly to the starting position as you inhale.', 'Now crunch over your torso forward and up around 3-4 inches to the left side and touch your left heel as you hold the contraction for a second. Exhale while performing this movement and then go back to the starting position as you inhale. Now that both heels have been touched, that is considered 1 repetition.', 'Continue alternating sides in this manner until all prescribed repetitions are done.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Alternate Incline Dumbbell Curl', 'Biceps', 'Dumbbell', 'Biceps', array['Forearms'], array['Sit down on an incline bench with a dumbbell in each hand being held at arms length. Tip: Keep the elbows close to the torso.This will be your starting position.', 'While holding the upper arm stationary, curl the right weight forward while contracting the biceps as you breathe out. As you do so, rotate the hand so that the palm is facing up. Continue the movement until your biceps is fully contracted and the dumbbells are at shoulder level. Hold the contracted position for a second as you squeeze the biceps. Tip: Only the forearms should move.', 'Slowly begin to bring the dumbbell back to starting position as your breathe in.', 'Repeat the movement with the left hand. This equals one repetition.', 'Continue alternating in this manner for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Front Raise', 'Shoulders', 'Dumbbell', 'Shoulders', array[]::text[], array['In a standing position, hold a pair of dumbbells at your side.', 'Keeping your elbows slightly bent, raise the weights directly in front of you to shoulder height, avoiding any swinging or cheating.', 'Return the weights to your side.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Arnold Dumbbell Press', 'Shoulders', 'Dumbbell', 'Shoulders', array['Triceps'], array['Sit on an exercise bench with back support and hold two dumbbells in front of you at about upper chest level with your palms facing your body and your elbows bent. Tip: Your arms should be next to your torso. The starting position should look like the contracted portion of a dumbbell curl.', 'Now to perform the movement, raise the dumbbells as you rotate the palms of your hands until they are facing forward.', 'Continue lifting the dumbbells until your arms are extended above you in straight arm position. Breathe out as you perform this portion of the movement.', 'After a second pause at the top, begin to lower the dumbbells to the original position by rotating the palms of your hands towards you. Tip: The left arm will be rotated in a counter clockwise manner while the right one will be rotated clockwise. Breathe in as you perform this portion of the movement.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Back Flyes - With Bands', 'Shoulders', 'Other', 'Shoulders', array['Middle back', 'Triceps'], array['Run a band around a stationary post like that of a squat rack.', 'Grab the band by the handles and stand back so that the tension in the band rises.', 'Extend and lift the arms straight in front of you. Tip: Your arms should be straight and parallel to the floor while perpendicular to your torso. Your feet should be firmly planted on the floor spread at shoulder width. This will be your starting position.', 'As you exhale, move your arms to the sides and back. Keep your arms extended and parallel to the floor. Continue the movement until the arms are extended to your sides.', 'After a pause, go back to the original position as you inhale.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Band Assisted Pull-Up', 'Lats', 'Other', 'Lats', array['Abdominals', 'Forearms', 'Middle back'], array['Choke the band around the center of the pullup bar. You can use different bands to provide varying levels of assistance.', 'Pull the end of the band down, and place one bent knee into the loop, ensuring it won''t slip out. Take a medium to wide grip on the bar. This will be your starting position.', 'Pull yourself upward by contracting the lats as you flex the elbow. The elbow should be driven to your side. Pull to the front, attempting to get your chin over the bar. Avoid swinging or jerking movements.', 'After a brief pause, return to the starting position.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Barbell Ab Rollout', 'Abdominals', 'Barbell', 'Abdominals', array['Lower back', 'Shoulders'], array['For this exercise you will need to get into a pushup position, but instead of having your hands of the floor, you will be grabbing on to an Olympic barbell (loaded with 5-10 lbs on each side) instead. This will be your starting position.', 'While keeping a slight arch on your back, lift your hips and roll the barbell towards your feet as you exhale. Tip: As you perform the movement, your glutes should be coming up, you should be keeping the abs tight and should maintain your back posture at all times. Also your arms should be staying perpendicular to the floor throughout the movement. If you don''t, you will work out your shoulders and back more than the abs.', 'After a second contraction at the top, start to roll the barbell back forward to the starting position slowly as you inhale.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Barbell Bench Press', 'Chest', 'Barbell', 'Chest', array['Shoulders', 'Triceps'], array['Lie back on a flat bench. Using a medium width grip (a grip that creates a 90-degree angle in the middle of the movement between the forearms and the upper arms), lift the bar from the rack and hold it straight over you with your arms locked. This will be your starting position.', 'From the starting position, breathe in and begin coming down slowly until the bar touches your middle chest.', 'After a brief pause, push the bar back to the starting position as you breathe out. Focus on pushing the bar using your chest muscles. Lock your arms and squeeze your chest in the contracted position at the top of the motion, hold for a second and then start coming down slowly again. Tip: Ideally, lowering the weight should take about twice as long as raising it.', 'Repeat the movement for the prescribed amount of repetitions.', 'When you are done, place the bar back in the rack.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Barbell Curl', 'Biceps', 'Barbell', 'Biceps', array['Forearms'], array['Stand up with your torso upright while holding a barbell at a shoulder-width grip. The palm of your hands should be facing forward and the elbows should be close to the torso. This will be your starting position.', 'While holding the upper arms stationary, curl the weights forward while contracting the biceps as you breathe out. Tip: Only the forearms should move.', 'Continue the movement until your biceps are fully contracted and the bar is at shoulder level. Hold the contracted position for a second and squeeze the biceps hard.', 'Slowly begin to bring the bar back to starting position as your breathe in.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Barbell Deadlift', 'Lower back', 'Barbell', 'Lower back', array['Calves', 'Forearms', 'Glutes', 'Hamstrings', 'Lats', 'Middle back', 'Quadriceps', 'Traps'], array['Stand in front of a loaded barbell.', 'While keeping the back as straight as possible, bend your knees, bend forward and grasp the bar using a medium (shoulder width) overhand grip. This will be the starting position of the exercise. Tip: If it is difficult to hold on to the bar with this grip, alternate your grip or use wrist straps.', 'While holding the bar, start the lift by pushing with your legs while simultaneously getting your torso to the upright position as you breathe out. In the upright position, stick your chest out and contract the back by bringing the shoulder blades back. Think of how the soldiers in the military look when they are in standing in attention.', 'Go back to the starting position by bending at the knees while simultaneously leaning the torso forward at the waist while keeping the back straight. When the weights on the bar touch the floor you are back at the starting position and ready to perform another repetition.', 'Perform the amount of repetitions prescribed in the program.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Barbell Full Squat', 'Quadriceps', 'Barbell', 'Quadriceps', array['Calves', 'Glutes', 'Hamstrings', 'Lower back'], array['This exercise is best performed inside a squat rack for safety purposes. To begin, first set the bar on a rack just above shoulder level. Once the correct height is chosen and the bar is loaded, step under the bar and place the back of your shoulders (slightly below the neck) across it.', 'Hold on to the bar using both arms at each side and lift it off the rack by first pushing with your legs and at the same time straightening your torso.', 'Step away from the rack and position your legs using a shoulder-width medium stance with the toes slightly pointed out. Keep your head up at all times and maintain a straight back. This will be your starting position.', 'Begin to slowly lower the bar by bending the knees and sitting back with your hips as you maintain a straight posture with the head up. Continue down until your hamstrings are on your calves. Inhale as you perform this portion of the movement.', 'Begin to raise the bar as you exhale by pushing the floor with the heel or middle of your foot as you straighten the legs and extend the hips to go back to the starting position.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Barbell Glute Bridge', 'Glutes', 'Barbell', 'Glutes', array['Calves', 'Hamstrings'], array['Begin seated on the ground with a loaded barbell over your legs. Using a fat bar or having a pad on the bar can greatly reduce the discomfort caused by this exercise. Roll the bar so that it is directly above your hips, and lay down flat on the floor.', 'Begin the movement by driving through with your heels, extending your hips vertically through the bar. Your weight should be supported by your upper back and the heels of your feet.', 'Extend as far as possible, then reverse the motion to return to the starting position.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Barbell Guillotine Bench Press', 'Chest', 'Barbell', 'Chest', array['Shoulders', 'Triceps'], array['Using a medium width grip (a grip that creates a 90-degree angle in the middle of the movement between the forearms and the upper arms), lift the bar from the rack and hold it straight over your neck with your arms locked. This will be your starting position.', 'As you breathe in, bring the bar down slowly until it is about 1 inch from your neck.', 'After a second pause, bring the bar back to the starting position as you breathe out and push the bar using your chest muscles. Lock your arms and squeeze your chest in the contracted position, hold for a second and then start coming down slowly again. It should take at least twice as long to go down than to come up.', 'Repeat the movement for the prescribed amount of repetitions.', 'When you are done, place the bar back in the rack.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Barbell Hack Squat', 'Quadriceps', 'Barbell', 'Quadriceps', array['Calves', 'Forearms', 'Hamstrings'], array['Stand up straight while holding a barbell behind you at arms length and your feet at shoulder width. Tip: A shoulder width grip is best with the palms of your hands facing back. You can use wrist wraps for this exercise for a better grip. This will be your starting position.', 'While keeping your head and eyes up and back straight, squat until your upper thighs are parallel to the floor. Breathe in as you slowly go down.', 'Pressing mainly with the heel of the foot and squeezing the thighs, go back up as you breathe out.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Barbell Hip Thrust', 'Glutes', 'Barbell', 'Glutes', array['Calves', 'Hamstrings'], array['Begin seated on the ground with a bench directly behind you. Have a loaded barbell over your legs. Using a fat bar or having a pad on the bar can greatly reduce the discomfort caused by this exercise.', 'Roll the bar so that it is directly above your hips, and lean back against the bench so that your shoulder blades are near the top of it.', 'Begin the movement by driving through your feet, extending your hips vertically through the bar. Your weight should be supported by your shoulder blades and your feet. Extend as far as possible, then reverse the motion to return to the starting position.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Barbell Incline Bench Press', 'Chest', 'Barbell', 'Chest', array['Shoulders', 'Triceps'], array['Lie back on an incline bench. Using a medium-width grip (a grip that creates a 90-degree angle in the middle of the movement between the forearms and the upper arms), lift the bar from the rack and hold it straight over you with your arms locked. This will be your starting position.', 'As you breathe in, come down slowly until you feel the bar on you upper chest.', 'After a second pause, bring the bar back to the starting position as you breathe out and push the bar using your chest muscles. Lock your arms in the contracted position, squeeze your chest, hold for a second and then start coming down slowly again. Tip: it should take at least twice as long to go down than to come up.', 'Repeat the movement for the prescribed amount of repetitions.', 'When you are done, place the bar back in the rack.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Barbell Incline Shoulder Raise', 'Shoulders', 'Barbell', 'Shoulders', array['Chest'], array['Lie back on an Incline Bench. Using a medium width grip (a grip that is slightly wider than shoulder width), lift the bar from the rack and hold it straight over you with your arms straight. This will be your starting position.', 'While keeping the arms straight, lift the bar by protracting your shoulder blades, raising the shoulders from the bench as you breathe out.', 'Bring back the bar to the starting position as you breathe in.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Barbell Lunge', 'Quadriceps', 'Barbell', 'Quadriceps', array['Calves', 'Glutes', 'Hamstrings'], array['This exercise is best performed inside a squat rack for safety purposes. To begin, first set the bar on a rack just below shoulder level. Once the correct height is chosen and the bar is loaded, step under the bar and place the back of your shoulders (slightly below the neck) across it.', 'Hold on to the bar using both arms at each side and lift it off the rack by first pushing with your legs and at the same time straightening your torso.', 'Step away from the rack and step forward with your right leg and squat down through your hips, while keeping the torso upright and maintaining balance. Inhale as you go down. Note: Do not allow your knee to go forward beyond your toes as you come down, as this will put undue stress on the knee joint. li>', 'Using mainly the heel of your foot, push up and go back to the starting position as you exhale.', 'Repeat the movement for the recommended amount of repetitions and then perform with the left leg.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Barbell Shoulder Press', 'Shoulders', 'Barbell', 'Shoulders', array['Chest', 'Triceps'], array['Sit on a bench with back support in a squat rack. Position a barbell at a height that is just above your head. Grab the barbell with a pronated grip (palms facing forward).', 'Once you pick up the barbell with the correct grip width, lift the bar up over your head by locking your arms. Hold at about shoulder level and slightly in front of your head. This is your starting position.', 'Lower the bar down to the shoulders slowly as you inhale.', 'Lift the bar back up to the starting position as you exhale.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Barbell Shrug', 'Traps', 'Barbell', 'Traps', array[]::text[], array['Stand up straight with your feet at shoulder width as you hold a barbell with both hands in front of you using a pronated grip (palms facing the thighs). Tip: Your hands should be a little wider than shoulder width apart. You can use wrist wraps for this exercise for a better grip. This will be your starting position.', 'Raise your shoulders up as far as you can go as you breathe out and hold the contraction for a second. Tip: Refrain from trying to lift the barbell by using your biceps.', 'Slowly return to the starting position as you breathe in.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Barbell Side Split Squat', 'Quadriceps', 'Barbell', 'Quadriceps', array['Calves', 'Hamstrings', 'Lower back'], array['Stand up straight while holding a barbell placed on the back of your shoulders (slightly below the neck). Your feet should be placed wide apart with the foot of the lead leg angled out to the side. This will be your starting position.', 'Lower your body towards the side of your angled foot by bending the knee and hip of your lead leg and while keeping the opposite leg only slightly bent. Breathe in as you lower your body.', 'Return to the starting position by extending the hip and knee of the lead leg. Breathe out as you perform this movement.', 'After performing the recommended amount of reps, repeat the movement with the opposite leg.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Barbell Squat', 'Quadriceps', 'Barbell', 'Quadriceps', array['Calves', 'Glutes', 'Hamstrings', 'Lower back'], array['This exercise is best performed inside a squat rack for safety purposes. To begin, first set the bar on a rack to just below shoulder level. Once the correct height is chosen and the bar is loaded, step under the bar and place the back of your shoulders (slightly below the neck) across it.', 'Hold on to the bar using both arms at each side and lift it off the rack by first pushing with your legs and at the same time straightening your torso.', 'Step away from the rack and position your legs using a shoulder width medium stance with the toes slightly pointed out. Keep your head up at all times and also maintain a straight back. This will be your starting position. (Note: For the purposes of this discussion we will use the medium stance described above which targets overall development; however you can choose any of the three stances discussed in the foot stances section).', 'Begin to slowly lower the bar by bending the knees and hips as you maintain a straight posture with the head up. Continue down until the angle between the upper leg and the calves becomes slightly less than 90-degrees. Inhale as you perform this portion of the movement. Tip: If you performed the exercise correctly, the front of the knees should make an imaginary straight line with the toes that is perpendicular to the front. If your knees are past that imaginary line (if they are past your toes) then you are placing undue stress on the knee and the exercise has been performed incorrectly.', 'Begin to raise the bar as you exhale by pushing the floor with the heel of your foot as you straighten the legs again and go back to the starting position.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Barbell Walking Lunge', 'Quadriceps', 'Barbell', 'Quadriceps', array['Calves', 'Glutes', 'Hamstrings'], array['Begin standing with your feet shoulder width apart and a barbell across your upper back.', 'Step forward with one leg, flexing the knees to drop your hips. Descend until your rear knee nearly touches the ground. Your posture should remain upright, and your front knee should stay above the front foot.', 'Drive through the heel of your lead foot and extend both knees to raise yourself back up.', 'Step forward with your rear foot, repeating the lunge on the opposite leg.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Bench Dips', 'Triceps', 'Bodyweight', 'Triceps', array['Chest', 'Shoulders'], array['For this exercise you will need to place a bench behind your back. With the bench perpendicular to your body, and while looking away from it, hold on to the bench on its edge with the hands fully extended, separated at shoulder width. The legs will be extended forward, bent at the waist and perpendicular to your torso. This will be your starting position.', 'Slowly lower your body as you inhale by bending at the elbows until you lower yourself far enough to where there is an angle slightly smaller than 90 degrees between the upper arm and the forearm. Tip: Keep the elbows as close as possible throughout the movement. Forearms should always be pointing down.', 'Using your triceps to bring your torso up again, lift yourself back to the starting position.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Bent-Arm Barbell Pullover', 'Lats', 'Barbell', 'Lats', array['Chest', 'Lats', 'Shoulders', 'Triceps'], array['Lie on a flat bench with a barbell using a shoulder grip width.', 'Hold the bar straight over your chest with a bend in your arms. This will be your starting position.', 'While keeping your arms in the bent arm position, lower the weight slowly in an arc behind your head while breathing in until you feel a stretch on the chest.', 'At that point, bring the barbell back to the starting position using the arc through which the weight was lowered and exhale as you perform this movement.', 'Hold the weight on the initial position for a second and repeat the motion for the prescribed number of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Bent-Arm Dumbbell Pullover', 'Chest', 'Dumbbell', 'Chest', array['Lats', 'Shoulders', 'Triceps'], array['Place a dumbbell standing up on a flat bench.', 'Ensuring that the dumbbell stays securely placed at the top of the bench, lie perpendicular to the bench (torso across it as in forming a cross) with only your shoulders lying on the surface. Hips should be below the bench and legs bent with feet firmly on the floor. The head will be off the bench as well.', 'Grasp the dumbbell with both hands and hold it straight over your chest with a bend in your arms. Both palms should be pressing against the underside one of the sides of the dumbbell. This will be your starting position. Caution: Always ensure that the dumbbell used for this exercise is secure. Using a dumbbell with loose plates can result in the dumbbell falling apart and falling on your face.', 'While keeping your arms locked in the bent arm position, lower the weight slowly in an arc behind your head while breathing in until you feel a stretch on the chest.', 'At that point, bring the dumbbell back to the starting position using the arc through which the weight was lowered and exhale as you perform this movement.', 'Hold the weight on the initial position for a second and repeat the motion for the prescribed number of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Bent Over Barbell Row', 'Middle back', 'Barbell', 'Middle back', array['Biceps', 'Lats', 'Shoulders'], array['Holding a barbell with a pronated grip (palms facing down), bend your knees slightly and bring your torso forward, by bending at the waist, while keeping the back straight until it is almost parallel to the floor. Tip: Make sure that you keep the head up. The barbell should hang directly in front of you as your arms hang perpendicular to the floor and your torso. This is your starting position.', 'Now, while keeping the torso stationary, breathe out and lift the barbell to you. Keep the elbows close to the body and only use the forearms to hold the weight. At the top contracted position, squeeze the back muscles and hold for a brief pause.', 'Then inhale and slowly lower the barbell back to the starting position.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Bent Press', 'Abdominals', 'Kettlebells', 'Abdominals', array['Glutes', 'Hamstrings', 'Lower back', 'Quadriceps', 'Shoulders', 'Triceps'], array['Clean a kettlebell to your shoulder. Clean the kettlebell to your shoulders by extending through the legs and hips as you raise the kettlebell towards your shoulder. The wrist should rotate as you do so. This will be your starting position.', 'Begin my leaning to the side opposite the kettlebell, continuing until you are able to touch the ground with your free hand, keeping your eyes on the kettlebell. As you do so, press the weight vertically be extending through the elbow, keeping your arm perpendicular to the ground.', 'Return to an upright position, with the kettlebell above your head. Return the kettlebell to the shoulder and repeat for the desired number of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Body-Up', 'Triceps', 'Bodyweight', 'Triceps', array['Abdominals', 'Forearms'], array['Assume a plank position on the ground. You should be supporting your bodyweight on your toes and forearms, keeping your torso straight. Your forearms should be shoulder-width apart. This will be your starting position.', 'Pressing your palms firmly into the ground, extend through the elbows to raise your body from the ground. Keep your torso rigid as you perform the movement.', 'Slowly lower your forearms back to the ground by allowing the elbows to flex.', 'Repeat.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Body Tricep Press', 'Triceps', 'Bodyweight', 'Triceps', array[]::text[], array['Position a bar in a rack at chest height.', 'Standing, take a shoulder width grip on the bar and step a yard or two back, feet together and arms extended so that you are leaning on the bar. This will be your starting position.', 'Begin by flexing the elbow, lowering yourself towards the bar.', 'Pause, and then reverse the motion by extending the elbows.', 'Progress from bodyweight by adding chains over your shoulders.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Bodyweight Squat', 'Quadriceps', 'Bodyweight', 'Quadriceps', array['Glutes', 'Hamstrings'], array['Stand with your feet shoulder width apart. You can place your hands behind your head. This will be your starting position.', 'Begin the movement by flexing your knees and hips, sitting back with your hips.', 'Continue down to full depth if you are able,and quickly reverse the motion until you return to the starting position. As you squat, keep your head and chest up and push your knees out.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Bodyweight Walking Lunge', 'Quadriceps', null, 'Quadriceps', array['Calves', 'Glutes', 'Hamstrings'], array['Begin standing with your feet shoulder width apart and your hands on your hips.', 'Step forward with one leg, flexing the knees to drop your hips. Descend until your rear knee nearly touches the ground. Your posture should remain upright, and your front knee should stay above the front foot.', 'Drive through the heel of your lead foot and extend both knees to raise yourself back up.', 'Step forward with your rear foot, repeating the lunge on the opposite leg.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Machine Fly', 'Chest', 'Machine', 'Chest', array[]::text[], array['Sit on the machine with your back flat on the pad.', 'Take hold of the handles. Tip: Your upper arms should be positioned parallel to the floor; adjust the machine accordingly. This will be your starting position.', 'Push the handles together slowly as you squeeze your chest in the middle. Breathe out during this part of the motion and hold the contraction for a second.', 'Return back to the starting position slowly as you inhale until your chest muscles are fully stretched.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Cable Chest Press', 'Chest', 'Cable', 'Chest', array['Shoulders', 'Triceps'], array['Adjust the weight to an appropriate amount and be seated, grasping the handles. Your upper arms should be about 45 degrees to the body, with your head and chest up. The elbows should be bent to about 90 degrees. This will be your starting position.', 'Begin by extending through the elbow, pressing the handles together straight in front of you. Keep your shoulder blades retracted as you execute the movement.', 'After pausing at full extension, return to th starting position, keeping tension on the cables.', 'You can also execute this movement with your back off the pad, at an incline or decline, or alternate hands.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Cable Crossover', 'Chest', 'Cable', 'Chest', array['Shoulders'], array['To get yourself into the starting position, place the pulleys on a high position (above your head), select the resistance to be used and hold the pulleys in each hand.', 'Step forward in front of an imaginary straight line between both pulleys while pulling your arms together in front of you. Your torso should have a small forward bend from the waist. This will be your starting position.', 'With a slight bend on your elbows in order to prevent stress at the biceps tendon, extend your arms to the side (straight out at both sides) in a wide arc until you feel a stretch on your chest. Breathe in as you perform this portion of the movement. Tip: Keep in mind that throughout the movement, the arms and torso should remain stationary; the movement should only occur at the shoulder joint.', 'Return your arms back to the starting position as you breathe out. Make sure to use the same arc of motion used to lower the weights.', 'Hold for a second at the starting position and repeat the movement for the prescribed amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Cable Crunch', 'Abdominals', 'Cable', 'Abdominals', array[]::text[], array['Kneel below a high pulley that contains a rope attachment.', 'Grasp cable rope attachment and lower the rope until your hands are placed next to your face.', 'Flex your hips slightly and allow the weight to hyperextend the lower back. This will be your starting position.', 'With the hips stationary, flex the waist as you contract the abs so that the elbows travel towards the middle of the thighs. Exhale as you perform this portion of the movement and hold the contraction for a second.', 'Slowly return to the starting position as you inhale. Tip: Make sure that you keep constant tension on the abs throughout the movement. Also, do not choose a weight so heavy that the lower back handles the brunt of the work.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Cable Hammer Curls', 'Biceps', 'Cable', 'Biceps', array[]::text[], array['Attach a rope attachment to a low pulley and stand facing the machine about 12 inches away from it.', 'Grasp the rope with a neutral (palms-in) grip and stand straight up keeping the natural arch of the back and your torso stationary.', 'Put your elbows in by your side and keep them there stationary during the entire movement. Tip: Only the forearms should move; not your upper arms. This will be your starting position.', 'Using your biceps, pull your arms up as you exhale until your biceps touch your forearms. Tip: Remember to keep the elbows in and your upper arms stationary.', 'After a 1 second contraction where you squeeze your biceps, slowly start to bring the weight back to the original position.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Cable Hip Adduction', 'Quadriceps', 'Cable', 'Quadriceps', array[]::text[], array['Stand in front of a low pulley facing forward with one leg next to the pulley and the other one away.', 'Attach the ankle cuff to the cable and also to the ankle of the leg that is next to the pulley.', 'Now step out and away from the stack with a wide stance and grasp the bar of the pulley system.', 'Stand on the foot that does not have the ankle cuff (the far foot) and allow the leg with the cuff to be pulled towards the low pulley. This will be your starting position.', 'Now perform the movement by moving the leg with the ankle cuff in front of the far leg by using the inner thighs to abduct the hip. Breathe out during this portion of the movement.', 'Slowly return to the starting position as you breathe in.', 'Repeat for the recommended amount of repetitions and then repeat the same movement with the opposite leg.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Cable One Arm Tricep Extension', 'Triceps', 'Cable', 'Triceps', array[]::text[], array['With your right hand, grasp a single handle attached to the high-cable pulley using a supinated (underhand; palms facing up) grip. You should be standing directly in front of the weight stack.', 'Now pull the handle down so that your upper arm and elbow are locked in to the side of your body. Your upper arm and forearm should form an acute angle (less than 90-degrees). You can keep the other arm by the waist and you can have one leg in front of you and the other one back for better balance. This will be your starting position.', 'As you contract the triceps, move the single handle attachment down to your side until your arm is straight. Breathe out as you perform this movement. Tip: Only the forearms should move. Your upper arms should remain stationary at all times.', 'Squeeze the triceps and hold for a second in this contracted position.', 'Slowly return the handle to the starting position.', 'Repeat for the recommended amount of repetitions and then perform the same movement with the other arm.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Cable Preacher Curl', 'Biceps', 'Cable', 'Biceps', array['Forearms'], array['Place a preacher bench about 2 feet in front of a pulley machine.', 'Attach a straight bar to the low pulley.', 'Sit at the preacher bench with your elbow and upper arms firmly on top of the bench pad and have someone hand you the bar from the low pulley.', 'Grab the bar and fully extend your arms on top of the preacher bench pad. This will be your starting position.', 'Now start pilling the weight up towards your shoulders and squeeze the biceps hard at the top of the movement. Exhale as you perform this motion. Also, hold for a second at the top.', 'Now slowly lower the weight to the starting position.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Cable Rear Delt Fly', 'Shoulders', 'Cable', 'Shoulders', array[]::text[], array['Adjust the pulleys to the appropriate height and adjust the weight. The pulleys should be above your head.', 'Grab the left pulley with your right hand and the right pulley with your left hand, crossing them in front of you. This will be your starting position.', 'Initiate the movement by moving your arms back and outward, keeping your arms straight as you execute the movement.', 'Pause at the end of the motion before returning the handles to the start position.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Cable Overhead Triceps Extension', 'Triceps', 'Cable', 'Triceps', array[]::text[], array['Attach a rope to the bottom pulley of the pulley machine.', 'Grasping the rope with both hands, extend your arms with your hands directly above your head using a neutral grip (palms facing each other). Your elbows should be in close to your head and the arms should be perpendicular to the floor with the knuckles aimed at the ceiling. This will be your starting position.', 'Slowly lower the rope behind your head as you hold the upper arms stationary. Inhale as you perform this movement and pause when your triceps are fully stretched.', 'Return to the starting position by flexing your triceps as you breathe out.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Cable Seated Crunch', 'Abdominals', 'Cable', 'Abdominals', array[]::text[], array['Seat on a flat bench with your back facing a high pulley.', 'Grasp the cable rope attachment with both hands (with the palms of the hands facing each other) and place your hands securely over both shoulders. Tip: Allow the weight to hyperextend the lower back slightly. This will be your starting position.', 'With the hips stationary, flex the waist so the elbows travel toward the hips. Breathe out as you perform this step.', 'As you inhale, go back to the initial position slowly.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Cable Wrist Curl', 'Forearms', 'Cable', 'Forearms', array[]::text[], array['Start out by placing a flat bench in front of a low pulley cable that has a straight bar attachment.', 'Use your arms to grab the cable bar with a narrow to shoulder width supinated grip (palms up) and bring them up so that your forearms are resting against the top of your thighs. Your wrists should be hanging just beyond your knees.', 'Start out by curling your wrist upwards and exhaling. Keep the contraction for a second.', 'Slowly lower your wrists back down to the starting position while inhaling.', 'Your forearms should be stationary as your wrist is the only movement needed to perform this exercise.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Calf Press', 'Calves', 'Machine', 'Calves', array[]::text[], array['Adjust the seat so that your legs are only slightly bent in the start position. The balls of your feet should be firmly on the platform.', 'Select an appropriate weight, and grasp the handles. This will be your starting position.', 'Straighten the legs by extending the knees, just barely lifting the weight from the stack. Your ankle should be fully flexed, toes pointing up. Execute the movement by pressing downward through the balls of your feet as far as possible.', 'After a brief pause, reverse the motion and repeat.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Calf Press On Leg Press Machine', 'Calves', 'Machine', 'Calves', array[]::text[], array['Using a leg press machine, sit down on the machine and place your legs on the platform directly in front of you at a medium (shoulder width) foot stance.', 'Lower the safety bars holding the weighted platform in place and press the platform all the way up until your legs are fully extended in front of you without locking your knees. (Note: In some leg press units you can leave the safety bars on for increased safety. If your leg press unit allows for this, then this is the preferred method of performing the exercise.) Your torso and the legs should make perfect 90-degree angle. Now carefully place your toes and balls of your feet on the lower portion of the platform with the heels extending off. Toes should be facing forward, outwards or inwards as described at the beginning of the chapter. This will be your starting position.', 'Press on the platform by raising your heels as you breathe out by extending your ankles as high as possible and flexing your calf. Ensure that the knee is kept stationary at all times. There should be no bending at any time. Hold the contracted position by a second before you start to go back down.', 'Go back slowly to the starting position as you breathe in by lowering your heels as you bend the ankles until calves are stretched.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Chin-Up', 'Lats', 'Bodyweight', 'Lats', array['Biceps', 'Forearms', 'Middle back'], array['Grab the pull-up bar with the palms facing your torso and a grip closer than the shoulder width.', 'As you have both arms extended in front of you holding the bar at the chosen grip width, keep your torso as straight as possible while creating a curvature on your lower back and sticking your chest out. This is your starting position. Tip: Keeping the torso as straight as possible maximizes biceps stimulation while minimizing back involvement.', 'As you breathe out, pull your torso up until your head is around the level of the pull-up bar. Concentrate on using the biceps muscles in order to perform the movement. Keep the elbows close to your body. Tip: The upper torso should remain stationary as it moves through space and only the arms should move. The forearms should do no other work other than hold the bar.', 'After a second of squeezing the biceps in the contracted position, slowly lower your torso back to the starting position; when your arms are fully extended. Breathe in as you perform this portion of the movement.', 'Repeat this motion for the prescribed amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Close-Grip Barbell Bench Press', 'Triceps', 'Barbell', 'Triceps', array['Chest', 'Shoulders'], array['Lie back on a flat bench. Using a close grip (around shoulder width), lift the bar from the rack and hold it straight over you with your arms locked. This will be your starting position.', 'As you breathe in, come down slowly until you feel the bar on your middle chest. Tip: Make sure that - as opposed to a regular bench press - you keep the elbows close to the torso at all times in order to maximize triceps involvement.', 'After a second pause, bring the bar back to the starting position as you breathe out and push the bar using your triceps muscles. Lock your arms in the contracted position, hold for a second and then start coming down slowly again. Tip: It should take at least twice as long to go down than to come up.', 'Repeat the movement for the prescribed amount of repetitions.', 'When you are done, place the bar back in the rack.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Close-Grip Dumbbell Press', 'Triceps', 'Dumbbell', 'Triceps', array['Chest', 'Shoulders'], array['Place a dumbbell standing up on a flat bench.', 'Ensuring that the dumbbell stays securely placed at the top of the bench, lie perpendicular to the bench with only your shoulders lying on the surface. Hips should be below the bench and your legs bent with your feet firmly on the floor.', 'Grasp the dumbbell with both hands and hold it straight over your chest at arm''s length. Both palms should be pressing against the underside of the sides of the dumbbell. This will be your starting position.', 'Initiate the movement by lowering the dumbbell to your chest.', 'Return to the starting position by extending the elbows.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Close-Grip EZ-Bar Press', 'Triceps', 'Barbell', 'Triceps', array['Chest', 'Shoulders'], array['Lie on a flat bench with an EZ bar loaded to an appropriate weight.', 'Using a narrow grip lift the bar and hold it straight over your torso with your elbows in. The arms should be perpendicular to the floor. This will be your starting position.', 'Now lower the bar down to your lower chest as you breathe in. Keep the elbows in as you perform this movement.', 'Using the triceps to push the bar back up, press it back to the starting position by extending the elbows as you exhale.', 'Repeat.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Close-Grip EZ Bar Curl', 'Biceps', 'Barbell', 'Biceps', array['Forearms'], array['Stand up with your torso upright while holding an E-Z Curl Bar at the closer inner handle. The palm of your hands should be facing forward and they should be slightly tilted inwards due to the shape of the bar. The elbows should be close to the torso. This will be your starting position.', 'While holding the upper arms stationary, curl the weights forward while contracting the biceps as you breathe out. Tip: Only the forearms should move.', 'Continue the movement until your biceps are fully contracted and the bar is at shoulder level. Hold the contracted position for a second and squeeze the biceps hard.', 'Slowly begin to bring the bar back to starting position as your breathe in.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Close-Grip Front Lat Pulldown', 'Lats', 'Cable', 'Lats', array['Biceps', 'Middle back', 'Shoulders'], array['Sit down on a pull-down machine with a wide bar attached to the top pulley. Make sure that you adjust the knee pad of the machine to fit your height. These pads will prevent your body from being raised by the resistance attached to the bar.', 'Grab the bar with the palms facing forward using the prescribed grip. Note on grips: For a wide grip, your hands need to be spaced out at a distance wider than your shoulder width. For a medium grip, your hands need to be spaced out at a distance equal to your shoulder width and for a close grip at a distance smaller than your shoulder width.', 'As you have both arms extended in front of you - while holding the bar at the chosen grip width - bring your torso back around 30 degrees or so while creating a curvature on your lower back and sticking your chest out. This is your starting position.', 'As you breathe out, bring the bar down until it touches your upper chest by drawing the shoulders and the upper arms down and back. Tip: Concentrate on squeezing the back muscles once you reach the full contracted position. The upper torso should remain stationary (only the arms should move). The forearms should do no other work except for holding the bar; therefore do not try to pull the bar down using the forearms.', 'After a second in the contracted position, while squeezing your shoulder blades together, slowly raise the bar back to the starting position when your arms are fully extended and the lats are fully stretched. Inhale during this portion of the movement.', '6. Repeat this motion for the prescribed amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Close-Grip Standing Barbell Curl', 'Biceps', 'Barbell', 'Biceps', array['Forearms'], array['Hold a barbell with both hands, palms up and a few inches apart.', 'Stand with your torso straight and your head up. Your feet should be about shoulder width and your elbows close to your torso. This will be your starting position. Tip: You will keep your upper arms and elbows stationary throughout the movement.', 'Curl the bar up in a semicircular motion until the forearms touch your biceps. Exhale as you perform this portion of the movement and contract your biceps hard for a second at the top. Tip: Avoid bending the back or using swinging motions as you lift the weight. Only the forearms should move.', 'Slowly go back down to the starting position as you inhale.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Concentration Curls', 'Biceps', 'Dumbbell', 'Biceps', array['Forearms'], array['Sit down on a flat bench with one dumbbell in front of you between your legs. Your legs should be spread with your knees bent and feet on the floor.', 'Use your right arm to pick the dumbbell up. Place the back of your right upper arm on the top of your inner right thigh. Rotate the palm of your hand until it is facing forward away from your thigh. Tip: Your arm should be extended and the dumbbell should be above the floor. This will be your starting position.', 'While holding the upper arm stationary, curl the weights forward while contracting the biceps as you breathe out. Only the forearms should move. Continue the movement until your biceps are fully contracted and the dumbbells are at shoulder level. Tip: At the top of the movement make sure that the little finger of your arm is higher than your thumb. This guarantees a good contraction. Hold the contracted position for a second as you squeeze the biceps.', 'Slowly begin to bring the dumbbells back to starting position as your breathe in. Caution: Avoid swinging motions at any time.', 'Repeat for the recommended amount of repetitions. Then repeat the movement with the left arm.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Cross-Body Crunch', 'Abdominals', 'Bodyweight', 'Abdominals', array[]::text[], array['Lie flat on your back and bend your knees about 60 degrees.', 'Keep your feet flat on the floor and place your hands loosely behind your head. This will be your starting position.', 'Now curl up and bring your right elbow and shoulder across your body while bring your left knee in toward your left shoulder at the same time. Reach with your elbow and try to touch your knee. Exhale as you perform this movement. Tip: Try to bring your shoulder up towards your knee rather than just your elbow and remember that the key is to contract the abs as you perform the movement; not just to move the elbow.', 'Now go back down to the starting position as you inhale and repeat with the left elbow and the right knee.', 'Continue alternating in this manner until all prescribed repetitions are done.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Cross Body Hammer Curl', 'Biceps', 'Dumbbell', 'Biceps', array['Forearms'], array['Stand up straight with a dumbbell in each hand. Your hands should be down at your side with your palms facing in.', 'While keeping your palms facing in and without twisting your arm, curl the dumbbell of the right arm up towards your left shoulder as you exhale. Touch the top of the dumbbell to your shoulder and hold the contraction for a second.', 'Slowly lower the dumbbell along the same path as you inhale and then repeat the same movement for the left arm.', 'Continue alternating in this fashion until the recommended amount of repetitions is performed for each arm.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Crunches', 'Abdominals', 'Bodyweight', 'Abdominals', array[]::text[], array['Lie flat on your back with your feet flat on the ground, or resting on a bench with your knees bent at a 90 degree angle. If you are resting your feet on a bench, place them three to four inches apart and point your toes inward so they touch.', 'Now place your hands lightly on either side of your head keeping your elbows in. Tip: Don''t lock your fingers behind your head.', 'While pushing the small of your back down in the floor to better isolate your abdominal muscles, begin to roll your shoulders off the floor.', 'Continue to push down as hard as you can with your lower back as you contract your abdominals and exhale. Your shoulders should come up off the floor only about four inches, and your lower back should remain on the floor. At the top of the movement, contract your abdominals hard and keep the contraction for a second. Tip: Focus on slow, controlled movement - don''t cheat yourself by using momentum.', 'After the one second contraction, begin to come down slowly again to the starting position as you inhale.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Dead Bug', 'Abdominals', 'Bodyweight', 'Abdominals', array[]::text[], array['Begin lying on your back with your hands extended above you toward the ceiling.', 'Bring your feet, knees, and hips up to 90 degrees.', 'Exhale hard to bring your ribcage down and flatten your back onto the floor, rotating your pelvis up and squeezing your glutes. Hold this position throughout the movement. This will be your starting position.', 'Initiate the exercise by extending one leg, straightening the knee and hip to bring the leg just above the ground.', 'Maintain the position of your lumbar and pelvis as you perform the movement, as your back is going to want to arch.', 'Stay tight and return the working leg to the starting position.', 'Repeat on the opposite side, alternating until the set is complete.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Decline Barbell Bench Press', 'Chest', 'Barbell', 'Chest', array['Shoulders', 'Triceps'], array['Secure your legs at the end of the decline bench and slowly lay down on the bench.', 'Using a medium width grip (a grip that creates a 90-degree angle in the middle of the movement between the forearms and the upper arms), lift the bar from the rack and hold it straight over you with your arms locked. The arms should be perpendicular to the floor. This will be your starting position. Tip: In order to protect your rotator cuff, it is best if you have a spotter help you lift the barbell off the rack.', 'As you breathe in, come down slowly until you feel the bar on your lower chest.', 'After a second pause, bring the bar back to the starting position as you breathe out and push the bar using your chest muscles. Lock your arms and squeeze your chest in the contracted position, hold for a second and then start coming down slowly again. Tip: It should take at least twice as long to go down than to come up).', 'Repeat the movement for the prescribed amount of repetitions.', 'When you are done, place the bar back in the rack.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Decline Crunch', 'Abdominals', 'Bodyweight', 'Abdominals', array[]::text[], array['Secure your legs at the end of the decline bench and lie down.', 'Now place your hands lightly on either side of your head keeping your elbows in. Tip: Don''t lock your fingers behind your head.', 'While pushing the small of your back down in the bench to better isolate your abdominal muscles, begin to roll your shoulders off it.', 'Continue to push down as hard as you can with your lower back as you contract your abdominals and exhale. Your shoulders should come up off the bench only about four inches, and your lower back should remain on the bench. At the top of the movement, contract your abdominals hard and keep the contraction for a second. Tip: Focus on slow, controlled movement - don''t cheat yourself by using momentum.', 'After the one second contraction, begin to come down slowly again to the starting position as you inhale.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Decline Dumbbell Bench Press', 'Chest', 'Dumbbell', 'Chest', array['Shoulders', 'Triceps'], array['Secure your legs at the end of the decline bench and lie down with a dumbbell on each hand on top of your thighs. The palms of your hand will be facing each other.', 'Once you are laying down, move the dumbbells in front of you at shoulder width.', 'Once at shoulder width, rotate your wrists forward so that the palms of your hands are facing away from you. This will be your starting position.', 'Bring down the weights slowly to your side as you breathe out. Keep full control of the dumbbells at all times. Tip: Throughout the motion, the forearms should always be perpendicular to the floor.', 'As you breathe out, push the dumbbells up using your pectoral muscles. Lock your arms in the contracted position, squeeze your chest, hold for a second and then start coming down slowly. Tip: It should take at least twice as long to go down than to come up..', 'Repeat the movement for the prescribed amount of repetitions of your training program.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Decline Dumbbell Flyes', 'Chest', 'Dumbbell', 'Chest', array[]::text[], array['Secure your legs at the end of the decline bench and lie down with a dumbbell on each hand on top of your thighs. The palms of your hand will be facing each other.', 'Once you are laying down, move the dumbbells in front of you at shoulder width. The palms of the hands should be facing each other and the arms should be perpendicular to the floor and fully extended. This will be your starting position.', 'With a slight bend on your elbows in order to prevent stress at the biceps tendon, lower your arms out at both sides in a wide arc until you feel a stretch on your chest. Breathe in as you perform this portion of the movement. Tip: Keep in mind that throughout the movement, the arms should remain stationary; the movement should only occur at the shoulder joint.', 'Return your arms back to the starting position as you squeeze your chest muscles and breathe out. Tip: Make sure to use the same arc of motion used to lower the weights.', 'Hold for a second at the contracted position and repeat the movement for the prescribed amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Decline Push-Up', 'Chest', null, 'Chest', array['Shoulders', 'Triceps'], array['Lie on the floor face down and place your hands about 36 inches apart while holding your torso up at arms length. Move your feet up to a box or bench. This will be your starting position.', 'Next, lower yourself downward until your chest almost touches the floor as you inhale.', 'Now breathe out and press your upper body back up to the starting position while squeezing your chest.', 'After a brief pause at the top contracted position, you can begin to lower yourself downward again for as many repetitions as needed.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Decline Smith Press', 'Chest', 'Machine', 'Chest', array['Shoulders', 'Triceps'], array['Place a decline bench underneath the Smith machine. Now place the barbell at a height that you can reach when lying down and your arms are almost fully extended. Using a pronated grip that is wider than shoulder width, unlock the bar from the rack and hold it straight over you with your arms extended. This will be your starting position.', 'As you inhale, lower the bar under control by allowing the elbows to flex, lightly contacting the torso.', 'After a brief pause, bring the bar back to the starting position by extending the elbows, exhaling as you do so.', 'Repeat the movement for the prescribed amount of repetitions.', 'When the set is complete, lock the bar back in the rack.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Dip Machine', 'Triceps', 'Machine', 'Triceps', array['Chest', 'Shoulders'], array['Sit securely in a dip machine, select the weight and firmly grasp the handles.', 'Now keep your elbows in at your sides in order to place emphasis on the triceps. The elbows should be bent at a 90 degree angle.', 'As you contract the triceps, extend your arms downwards as you exhale. Tip: At the bottom of the movement, focus on keeping a little bend in your arms to keep tension on the triceps muscle.', 'Now slowly let your arms come back up to the starting position as you inhale.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Dips - Chest Version', 'Chest', 'Other', 'Chest', array['Shoulders', 'Triceps'], array['For this exercise you will need access to parallel bars. To get yourself into the starting position, hold your body at arms length (arms locked) above the bars.', 'While breathing in, lower yourself slowly with your torso leaning forward around 30 degrees or so and your elbows flared out slightly until you feel a slight stretch in the chest.', 'Once you feel the stretch, use your chest to bring your body back to the starting position as you breathe out. Tip: Remember to squeeze the chest at the top of the movement for a second.', 'Repeat the movement for the prescribed amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Dips - Triceps Version', 'Triceps', 'Bodyweight', 'Triceps', array['Chest', 'Shoulders'], array['To get into the starting position, hold your body at arm''s length with your arms nearly locked above the bars.', 'Now, inhale and slowly lower yourself downward. Your torso should remain upright and your elbows should stay close to your body. This helps to better focus on tricep involvement. Lower yourself until there is a 90 degree angle formed between the upper arm and forearm.', 'Then, exhale and push your torso back up using your triceps to bring your body back to the starting position.', 'Repeat the movement for the prescribed amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Dumbbell Bench Press', 'Chest', 'Dumbbell', 'Chest', array['Shoulders', 'Triceps'], array['Lie down on a flat bench with a dumbbell in each hand resting on top of your thighs. The palms of your hands will be facing each other.', 'Then, using your thighs to help raise the dumbbells up, lift the dumbbells one at a time so that you can hold them in front of you at shoulder width.', 'Once at shoulder width, rotate your wrists forward so that the palms of your hands are facing away from you. The dumbbells should be just to the sides of your chest, with your upper arm and forearm creating a 90 degree angle. Be sure to maintain full control of the dumbbells at all times. This will be your starting position.', 'Then, as you breathe out, use your chest to push the dumbbells up. Lock your arms at the top of the lift and squeeze your chest, hold for a second and then begin coming down slowly. Tip: Ideally, lowering the weight should take about twice as long as raising it.', 'Repeat the movement for the prescribed amount of repetitions of your training program.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Dumbbell Bicep Curl', 'Biceps', 'Dumbbell', 'Biceps', array['Forearms'], array['Stand up straight with a dumbbell in each hand at arm''s length. Keep your elbows close to your torso and rotate the palms of your hands until they are facing forward. This will be your starting position.', 'Now, keeping the upper arms stationary, exhale and curl the weights while contracting your biceps. Continue to raise the weights until your biceps are fully contracted and the dumbbells are at shoulder level. Hold the contracted position for a brief pause as you squeeze your biceps.', 'Then, inhale and slowly begin to lower the dumbbells back to the starting position.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Dumbbell Flyes', 'Chest', 'Dumbbell', 'Chest', array[]::text[], array['Lie down on a flat bench with a dumbbell on each hand resting on top of your thighs. The palms of your hand will be facing each other.', 'Then using your thighs to help raise the dumbbells, lift the dumbbells one at a time so you can hold them in front of you at shoulder width with the palms of your hands facing each other. Raise the dumbbells up like you''re pressing them, but stop and hold just before you lock out. This will be your starting position.', 'With a slight bend on your elbows in order to prevent stress at the biceps tendon, lower your arms out at both sides in a wide arc until you feel a stretch on your chest. Breathe in as you perform this portion of the movement. Tip: Keep in mind that throughout the movement, the arms should remain stationary; the movement should only occur at the shoulder joint.', 'Return your arms back to the starting position as you squeeze your chest muscles and breathe out. Tip: Make sure to use the same arc of motion used to lower the weights.', 'Hold for a second at the contracted position and repeat the movement for the prescribed amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Dumbbell Incline Row', 'Middle back', 'Dumbbell', 'Middle back', array['Biceps', 'Forearms', 'Lats', 'Shoulders'], array['Using a neutral grip, lean into an incline bench.', 'Take a dumbbell in each hand with a neutral grip, beginning with the arms straight. This will be your starting position.', 'Retract the shoulder blades and flex the elbows to row the dumbbells to your side.', 'Pause at the top of the motion, and then return to the starting position.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Dumbbell Lunges', 'Quadriceps', 'Dumbbell', 'Quadriceps', array['Calves', 'Glutes', 'Hamstrings'], array['Stand with your torso upright holding two dumbbells in your hands by your sides. This will be your starting position.', 'Step forward with your right leg around 2 feet or so from the foot being left stationary behind and lower your upper body down, while keeping the torso upright and maintaining balance. Inhale as you go down. Note: As in the other exercises, do not allow your knee to go forward beyond your toes as you come down, as this will put undue stress on the knee joint. Make sure that you keep your front shin perpendicular to the ground.', 'Using mainly the heel of your foot, push up and go back to the starting position as you exhale.', 'Repeat the movement for the recommended amount of repetitions and then perform with the left leg.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Dumbbell Lying Rear Lateral Raise', 'Shoulders', 'Dumbbell', 'Shoulders', array[]::text[], array['While holding a dumbbell in each hand, lay with your chest down on a slightly inclined (around 15 degrees when measured from the floor) adjustable bench.', 'Position the palms of the hands in a neutral manner (palms facing your torso) as you keep the arms extended with the elbows slightly bent. This will be your starting position.', 'Now raise the arms to the side until your elbows are at shoulder height and your arms are roughly parallel to the floor as you exhale. Tip: Maintain your arms perpendicular to the torso while keeping them extended throughout the movement. Also, keep the contraction at the top for a second.', 'Slowly lower the dumbbells to the starting position as you inhale.', 'Repeat for the recommended amount of repetitions and then switch to the other arm.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Dumbbell One-Arm Shoulder Press', 'Shoulders', 'Dumbbell', 'Shoulders', array['Triceps'], array['Grab a dumbbell and either sit on a military press bench or a utility bench that has a back support on it as you place the dumbbells upright on top of your thighs or stand up straight.', 'Clean the dumbbell up to bring it to shoulder height. The other hand can be kept fully extended to the side, by the waist or grabbing a fixed surface.', 'Rotate the wrist so that the palm of your hand is facing forward. This is your starting position.', 'As you exhale, push the dumbbell up until your arm is fully extended.', 'After a second pause, slowly come down back to the starting position as you inhale.', 'Repeat for the recommended amount of repetitions and then switch arms.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Dumbbell One-Arm Triceps Extension', 'Triceps', 'Dumbbell', 'Triceps', array[]::text[], array['Grab a dumbbell and either sit on a military press bench or a utility bench that has a back support on it as you place the dumbbells upright on top of your thighs or stand up straight.', 'Clean the dumbbell up to bring it to shoulder height and then extend the arm over your head so that the whole arm is perpendicular to the floor and next to your head. The dumbbell should be on top of you. The other hand can be kept fully extended to the side, by the waist, supporting the upper arm that has the dumbbell or grabbing a fixed surface.', 'Rotate the wrist so that the palm of your hand is facing forward and the pinkie is facing the ceiling. This will be your starting position.', 'Slowly lower the dumbbell behind your head as you hold the upper arm stationary. Inhale as you perform this movement and pause when your triceps are fully stretched.', 'Return to the starting position by flexing your triceps as you breathe out. Tip: It is imperative that only the forearm moves. The upper arm should remain at all times stationary next to your head.', 'Repeat for the recommended amount of repetitions and switch arms.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Dumbbell Rear Lunge', 'Quadriceps', 'Dumbbell', 'Quadriceps', array['Calves', 'Glutes', 'Hamstrings'], array['Stand with your torso upright holding two dumbbells in your hands by your sides. This will be your starting position.', 'Step backward with your right leg around two feet or so from the left foot and lower your upper body down, while keeping the torso upright and maintaining balance. Inhale as you go down. Tip: As in the other exercises, do not allow your knee to go forward beyond your toes as you come down, as this will put undue stress on the knee joint. Make sure that you keep your front shin perpendicular to the ground. Keep the torso upright during the lunge; flexible hip flexors are important. A long lunge emphasizes the Gluteus Maximus; a short lunge emphasizes Quadriceps.', 'Push up and go back to the starting position as you exhale. Tip: Use the ball of your feet to push in order to accentuate the quadriceps. To focus on the glutes, press with your heels.', 'Now repeat with the opposite leg.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Dumbbell Shoulder Press', 'Shoulders', 'Dumbbell', 'Shoulders', array['Triceps'], array['While holding a dumbbell in each hand, sit on a military press bench or utility bench that has back support. Place the dumbbells upright on top of your thighs.', 'Now raise the dumbbells to shoulder height one at a time using your thighs to help propel them up into position.', 'Make sure to rotate your wrists so that the palms of your hands are facing forward. This is your starting position.', 'Now, exhale and push the dumbbells upward until they touch at the top.', 'Then, after a brief pause at the top contracted position, slowly lower the weights back down to the starting position while inhaling.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Dumbbell Shrug', 'Traps', 'Dumbbell', 'Traps', array[]::text[], array['Stand erect with a dumbbell on each hand (palms facing your torso), arms extended on the sides.', 'Lift the dumbbells by elevating the shoulders as high as possible while you exhale. Hold the contraction at the top for a second. Tip: The arms should remain extended at all times. Refrain from using the biceps to help lift the dumbbells. Only the shoulders should be moving up and down.', 'Lower the dumbbells back to the original position.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Dumbbell Side Bend', 'Abdominals', 'Dumbbell', 'Abdominals', array[]::text[], array['Stand up straight while holding a dumbbell on the left hand (palms facing the torso) as you have the right hand holding your waist. Your feet should be placed at shoulder width. This will be your starting position.', 'While keeping your back straight and your head up, bend only at the waist to the right as far as possible. Breathe in as you bend to the side. Then hold for a second and come back up to the starting position as you exhale. Tip: Keep the rest of the body stationary.', 'Now repeat the movement but bending to the left instead. Hold for a second and come back to the starting position.', 'Repeat for the recommended amount of repetitions and then change hands.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Dumbbell Squat', 'Quadriceps', 'Dumbbell', 'Quadriceps', array['Calves', 'Glutes', 'Hamstrings', 'Lower back'], array['Stand up straight while holding a dumbbell on each hand (palms facing the side of your legs).', 'Position your legs using a shoulder width medium stance with the toes slightly pointed out. Keep your head up at all times as looking down will get you off balance and also maintain a straight back. This will be your starting position. Note: For the purposes of this discussion we will use the medium stance described above which targets overall development; however you can choose any of the three stances discussed in the foot stances section.', 'Begin to slowly lower your torso by bending the knees as you maintain a straight posture with the head up. Continue down until your thighs are parallel to the floor. Tip: If you performed the exercise correctly, the front of the knees should make an imaginary straight line with the toes that is perpendicular to the front. If your knees are past that imaginary line (if they are past your toes) then you are placing undue stress on the knee and the exercise has been performed incorrectly.', 'Begin to raise your torso as you exhale by pushing the floor with the heel of your foot mainly as you straighten the legs again and go back to the starting position.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Dumbbell Step Ups', 'Quadriceps', 'Dumbbell', 'Quadriceps', array['Calves', 'Glutes', 'Hamstrings'], array['Stand up straight while holding a dumbbell on each hand (palms facing the side of your legs).', 'Place the right foot on the elevated platform. Step on the platform by extending the hip and the knee of your right leg. Use the heel mainly to lift the rest of your body up and place the foot of the left leg on the platform as well. Breathe out as you execute the force required to come up.', 'Step down with the left leg by flexing the hip and knee of the right leg as you inhale. Return to the original standing position by placing the right foot of to next to the left foot on the initial position.', 'Repeat with the right leg for the recommended amount of repetitions and then perform with the left leg.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('EZ-Bar Curl', 'Biceps', 'Barbell', 'Biceps', array[]::text[], array['Stand up straight while holding an EZ curl bar at the wide outer handle. The palms of your hands should be facing forward and slightly tilted inward due to the shape of the bar. Keep your elbows close to your torso. This will be your starting position.', 'Now, while keeping your upper arms stationary, exhale and curl the weights forward while contracting the biceps. Focus on only moving your forearms.', 'Continue to raise the weight until your biceps are fully contracted and the bar is at shoulder level. Hold the top contracted position for a moment and squeeze the biceps.', 'Then inhale and slowly lower the bar back to the starting position.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('EZ-Bar Skullcrusher', 'Triceps', 'Barbell', 'Triceps', array['Forearms'], array['Using a close grip, lift the EZ bar and hold it with your elbows in as you lie on the bench. Your arms should be perpendicular to the floor. This will be your starting position.', 'Keeping the upper arms stationary, lower the bar by allowing the elbows to flex. Inhale as you perform this portion of the movement. Pause once the bar is directly above the forehead.', 'Lift the bar back to the starting position by extending the elbow and exhaling.', 'Repeat.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Exercise Ball Crunch', 'Abdominals', 'Other', 'Abdominals', array[]::text[], array['Lie on an exercise ball with your lower back curvature pressed against the spherical surface of the ball. Your feet should be bent at the knee and pressed firmly against the floor. The upper torso should be hanging off the top of the ball. The arms should either be kept alongside the body or crossed on top of your chest as these positions avoid neck strains (as opposed to the hands behind the back of the head position).', 'Lower your torso into a stretch position keeping the neck stationary at all times. This will be your starting position.', 'With the hips stationary, flex the waist by contracting the abdominals and curl the shoulders and trunk upward until you feel a nice contraction on your abdominals. The arms should simply slide up the side of your legs if you have them at the side or just stay on top of your chest if you have them crossed. The lower back should always stay in contact with the ball. Exhale as you perform this movement and hold the contraction for a second.', 'As you inhale, go back to the starting position.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Exercise Ball Pull-In', 'Abdominals', 'Other', 'Abdominals', array[]::text[], array['Place an exercise ball nearby and lay on the floor in front of it with your hands on the floor shoulder width apart in a push-up position.', 'Now place your lower shins on top of an exercise ball. Tip: At this point your legs should be fully extended with the shins on top of the ball and the upper body should be in a push-up type of position being supported by your two extended arms in front of you. This will be your starting position.', 'While keeping your back completely straight and the upper body stationary, pull your knees in towards your chest as you exhale, allowing the ball to roll forward under your ankles. Squeeze your abs and hold that position for a second.', 'Now slowly straighten your legs, rolling the ball back to the starting position as you inhale.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('External Rotation', 'Shoulders', 'Dumbbell', 'Shoulders', array[]::text[], array['Lie sideways on a flat bench with one arm holding a dumbbell and the other hand on top of the bench folded so that you can rest your head on it.', 'Bend the elbows of the arm holding the dumbbell so that it creates a 90-degree angle between the upper arm and the forearm. Tip: Keep the arm parallel to your torso.', 'Now bend the elbow while keeping the upper arm stationary. In this manner, the forearm will be parallel to the floor and perpendicular to your torso (Tip: So the forearm will be directly in front of you). The upper arm will be stationary by your torso and should be parallel to the floor (aligned with your torso at all times). This will be your starting position.', 'As you breathe out, externally rotate your forearm so that the dumbbell is lifted up in a semicircle motion as you maintain the 90 degree angle bend between the upper arms and the forearm. You will continue this external rotation until the forearm is perpendicular to the floor and the torso pointing towards the ceiling. At this point you will hold the contraction for a second.', 'As you breathe in, slowly go back to the starting position.', 'Repeat for the recommended amount of repetitions and then switch to the other arm.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('External Rotation with Band', 'Shoulders', 'Other', 'Shoulders', array[]::text[], array['Choke the band around a post. The band should be at the same height as your elbow. Stand with your left side to the band a couple of feet away.', 'Grasp the end of the band with your right hand, and keep your elbow pressed firmly to your side. We recommend you hold a pad or foam roll in place with your elbow to keep it firmly in position.', 'With your upper arm in position, your elbow should be flexed to 90 degrees with your hand reaching across the front of your torso. This will be your starting position.', 'Execute the movement by rotating your arm in a backhand motion, keeping your elbow in place.', 'Continue as far as you are able, pause, and then return to the starting position.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Face Pull', 'Shoulders', 'Cable', 'Shoulders', array['Middle back'], array['Facing a high pulley with a rope or dual handles attached, pull the weight directly towards your face, separating your hands as you do so. Keep your upper arms parallel to the ground.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Farmer''s Walk', 'Forearms', 'Dumbbell', 'Forearms', array['Abdominals', 'Glutes', 'Hamstrings', 'Lower back', 'Quadriceps', 'Traps'], array['There are various implements that can be used for the farmers walk. These can also be performed with heavy dumbbells or short bars if these implements aren''t available. Begin by standing between the implements.', 'After gripping the handles, lift them up by driving through your heels, keeping your back straight and your head up.', 'Walk taking short, quick steps, and don''t forget to breathe. Move for a given distance, typically 50-100 feet, as fast as possible.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Finger Curls', 'Forearms', 'Barbell', 'Forearms', array[]::text[], array['Hold a barbell with both hands and your palms facing up; hands spaced about shoulder width.', 'Place your feet flat on the floor, at a distance that is slightly wider than shoulder width apart. This will be your starting position.', 'Lower the bar as far as possible by extending the fingers. Allowing the bar to roll down the hands, catch the bar with the final joint in the fingers.', 'Now curl bar up as high as possible by closing your hands while exhaling. Hold the contraction at the top.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Flat Bench Cable Flys', 'Chest', 'Cable', 'Chest', array[]::text[], array['Position a flat bench between two low pulleys so that when you are laying on it, your chest will be lined up with the cable pulleys.', 'Lay flat on the bench and keep your feet on the ground.', 'Have someone hand you the handles on each hand. You will grab each single handle attachment with a palms up grip.', 'Extend your arms by your side with a slight bend on your elbows. Tip: You will keep this bend constant through the whole movement. Your arms should be parallel to the floor. This is your starting position.', 'Now start lifting the arms in a semi-circle motion directly in front of you by pulling the cables together until both hands meet at the top of the movement. Squeeze your chest as you perform this motion and breathe out during this movement. Also, hold the contraction for a second at the top. Tip: When performed correctly, at the top position of this movement, your arms should be perpendicular to your torso and the floor touching above your chest.', 'Slowly come back to the starting position.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Flat Bench Lying Leg Raise', 'Abdominals', 'Bodyweight', 'Abdominals', array[]::text[], array['Lie with your back flat on a bench and your legs extended in front of you off the end.', 'Place your hands either under your glutes with your palms down or by the sides holding on to the bench. This will be your starting position.', 'As you keep your legs extended, straight as possible with your knees slightly bent but locked raise your legs until they make a 90-degree angle with the floor. Exhale as you perform this portion of the movement and hold the contraction at the top for a second.', 'Now, as you inhale, slowly lower your legs back down to the starting position.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Floor Glute-Ham Raise', 'Hamstrings', null, 'Hamstrings', array['Calves', 'Glutes'], array['You can use a partner for this exercise or brace your feet under something stable.', 'Begin on your knees with your upper legs and torso upright. If using a partner, they will firmly hold your feet to keep you in position. This will be your starting position.', 'Lower yourself by extending at the knee, taking care to NOT flex the hips as you go forward.', 'Place your hands in front of you as you reach the floor. This movement is very difficult and you may be unable to do it unaided. Use your arms to lightly push off the floor to aid your return to the starting position.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Flutter Kicks', 'Glutes', 'Bodyweight', 'Glutes', array['Hamstrings'], array['On a flat bench lie facedown with the hips on the edge of the bench, the legs straight with toes high off the floor and with the arms on top of the bench holding on to the front edge.', 'Squeeze your glutes and hamstrings and straighten the legs until they are level with the hips. This will be your starting position.', 'Start the movement by lifting the left leg higher than the right leg.', 'Then lower the left leg as you lift the right leg.', 'Continue alternating in this manner (as though you are doing a flutter kick in water) until you have done the recommended amount of repetitions for each leg. Make sure that you keep a controlled movement at all times. Tip: You will breathe normally as you perform this movement.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Front Barbell Squat', 'Quadriceps', 'Barbell', 'Quadriceps', array['Calves', 'Glutes', 'Hamstrings'], array['This exercise is best performed inside a squat rack for safety purposes. To begin, first set the bar on a rack that best matches your height. Once the correct height is chosen and the bar is loaded, bring your arms up under the bar while keeping the elbows high and the upper arm slightly above parallel to the floor. Rest the bar on top of the deltoids and cross your arms while grasping the bar for total control.', 'Lift the bar off the rack by first pushing with your legs and at the same time straightening your torso.', 'Step away from the rack and position your legs using a shoulder width medium stance with the toes slightly pointed out. Keep your head up at all times as looking down will get you off balance and also maintain a straight back. This will be your starting position. (Note: For the purposes of this discussion we will use the medium stance described above which targets overall development; however you can choose any of the three stances described in the foot positioning section).', 'Begin to slowly lower the bar by bending the knees as you maintain a straight posture with the head up. Continue down until the angle between the upper leg and the calves becomes slightly less than 90-degrees (which is the point in which the upper legs are below parallel to the floor). Inhale as you perform this portion of the movement. Tip: If you performed the exercise correctly, the front of the knees should make an imaginary straight line with the toes that is perpendicular to the front. If your knees are past that imaginary line (if they are past your toes) then you are placing undue stress on the knee and the exercise has been performed incorrectly.', 'Begin to raise the bar as you exhale by pushing the floor mainly with the middle of your foot as you straighten the legs again and go back to the starting position.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Front Cable Raise', 'Shoulders', 'Cable', 'Shoulders', array[]::text[], array['Select the weight on a low pulley machine and grasp the single hand cable attachment that is attached to the low pulley with your left hand.', 'Face away from the pulley and put your arm straight down with the hand cable attachment in front of your thighs at arms'' length with the palms of the hand facing your thighs. This will be your starting position.', 'While maintaining the torso stationary (no swinging), lift the left arm to the front with a slight bend on the elbow and the palms of the hand always faces down. Continue to go up until you arm is slightly above parallel to the floor. Exhale as you execute this portion of the movement and pause for a second at the top.', 'Now as you inhale lower the arm back down slowly to the starting position.', 'Once all of the recommended amount of repetitions have been performed for this arm, switch arms and perform the exercise with the right one.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Front Dumbbell Raise', 'Shoulders', 'Dumbbell', 'Shoulders', array[]::text[], array['Pick a couple of dumbbells and stand with a straight torso and the dumbbells on front of your thighs at arms length with the palms of the hand facing your thighs. This will be your starting position.', 'While maintaining the torso stationary (no swinging), lift the left dumbbell to the front with a slight bend on the elbow and the palms of the hands always facing down. Continue to go up until you arm is slightly above parallel to the floor. Exhale as you execute this portion of the movement and pause for a second at the top. Inhale after the second pause.', 'Now lower the dumbbell back down slowly to the starting position as you simultaneously lift the right dumbbell.', 'Continue alternating in this fashion until all of the recommended amount of repetitions have been performed for each arm.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Front Plate Raise', 'Shoulders', 'Other', 'Shoulders', array[]::text[], array['While standing straight, hold a barbell plate in both hands at the 3 and 9 o''clock positions. Your palms should be facing each other and your arms should be extended and locked with a slight bend at the elbows and the plate should be down near your waist in front of you as far as you can go. Tip: The arms will remain in this position throughout the exercise. This will be your starting position.', 'Slowly raise the plate as you exhale until it is a little above shoulder level. Hold the contraction for a second. Tip: make sure that you do not swing the weight or bend at the elbows. Your torso should remain stationary throughout the movement as well.', 'As you inhale, slowly lower the plate back down to the starting position.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Glute Ham Raise', 'Hamstrings', 'Machine', 'Hamstrings', array['Calves', 'Glutes'], array['Begin by adjusting the equipment to fit your body. Place your feet against the footplate in between the rollers as you lie facedown. Your knees should be just behind the pad.', 'Start from the bottom of the movement. Keep your back arched as you begin the movement by flexing the knees. Drive your toes into the foot plate as you do so. Keep your upper body straight, and continue until your body is upright.', 'Return to the starting position, keeping your descent under control.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Glute Kickback', 'Glutes', 'Bodyweight', 'Glutes', array['Hamstrings'], array['Kneel on the floor or an exercise mat and bend at the waist with your arms extended in front of you (perpendicular to the torso) in order to get into a kneeling push-up position but with the arms spaced at shoulder width. Your head should be looking forward and the bend of the knees should create a 90-degree angle between the hamstrings and the calves. This will be your starting position.', 'As you exhale, lift up your right leg until the hamstrings are in line with the back while maintaining the 90-degree angle bend. Contract the glutes throughout this movement and hold the contraction at the top for a second. Tip: At the end of the movement the upper leg should be parallel to the floor while the calf should be perpendicular to it.', 'Go back to the initial position as you inhale and now repeat with the left leg.', 'Continue to alternate legs until all of the recommended repetitions have been performed.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Goblet Squat', 'Quadriceps', 'Kettlebells', 'Quadriceps', array['Calves', 'Glutes', 'Hamstrings', 'Shoulders'], array['Stand holding a light kettlebell by the horns close to your chest. This will be your starting position.', 'Squat down between your legs until your hamstrings are on your calves. Keep your chest and head up and your back straight.', 'At the bottom position, pause and use your elbows to push your knees out. Return to the starting position, and repeat for 10-20 repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Good Morning', 'Hamstrings', 'Barbell', 'Hamstrings', array['Abdominals', 'Glutes', 'Lower back'], array['Begin with a bar on a rack at shoulder height. Rack the bar across the rear of your shoulders as you would a power squat, not on top of your shoulders. Keep your back tight, shoulder blades pinched together, and your knees slightly bent. Step back from the rack.', 'Begin by bending at the hips, moving them back as you bend over to near parallel. Keep your back arched and your cervical spine in proper alignment.', 'Reverse the motion by extending through the hips with your glutes and hamstrings. Continue until you have returned to the starting position.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Hack Squat', 'Quadriceps', 'Machine', 'Quadriceps', array['Calves', 'Glutes', 'Hamstrings'], array['Place the back of your torso against the back pad of the machine and hook your shoulders under the shoulder pads provided.', 'Position your legs in the platform using a shoulder width medium stance with the toes slightly pointed out. Tip: Keep your head up at all times and also maintain the back on the pad at all times.', 'Place your arms on the side handles of the machine and disengage the safety bars (which on most designs is done by moving the side handles from a facing front position to a diagonal position).', 'Now straighten your legs without locking the knees. This will be your starting position. (Note: For the purposes of this discussion we will use the medium stance described above which targets overall development; however you can choose any of the three stances described in the foot positioning section).', 'Begin to slowly lower the unit by bending the knees as you maintain a straight posture with the head up (back on the pad at all times). Continue down until the angle between the upper leg and the calves becomes slightly less than 90-degrees (which is the point in which the upper legs are below parallel to the floor). Inhale as you perform this portion of the movement. Tip: If you performed the exercise correctly, the front of the knees should make an imaginary straight line with the toes that is perpendicular to the front. If your knees are past that imaginary line (if they are past your toes) then you are placing undue stress on the knee and the exercise has been performed incorrectly.', 'Begin to raise the unit as you exhale by pushing the floor with mainly with the heel of your foot as you straighten the legs again and go back to the starting position.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Hammer Curls', 'Biceps', 'Dumbbell', 'Biceps', array[]::text[], array['Stand up with your torso upright and a dumbbell on each hand being held at arms length. The elbows should be close to the torso.', 'The palms of the hands should be facing your torso. This will be your starting position.', 'Now, while holding your upper arm stationary, exhale and curl the weight forward while contracting the biceps. Continue to raise the weight until the biceps are fully contracted and the dumbbell is at shoulder level. Hold the contracted position for a brief moment as you squeeze the biceps. Tip: Focus on keeping the elbow stationary and only moving your forearm.', 'After the brief pause, inhale and slowly begin the lower the dumbbells back down to the starting position.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Handstand Push-Ups', 'Shoulders', 'Bodyweight', 'Shoulders', array['Triceps'], array['With your back to the wall bend at the waist and place both hands on the floor at shoulder width.', 'Kick yourself up against the wall with your arms straight. Your body should be upside down with the arms and legs fully extended. Keep your whole body as straight as possible. Tip: If doing this for the first time, have a spotter help you. Also, make sure that you keep facing the wall with your head, rather than looking down.', 'Slowly lower yourself to the ground as you inhale until your head almost touches the floor. Tip: It is of utmost importance that you come down slow in order to avoid head injury.', 'Push yourself back up slowly as you exhale until your elbows are nearly locked.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Hang Clean', 'Quadriceps', 'Barbell', 'Quadriceps', array['Calves', 'Forearms', 'Glutes', 'Hamstrings', 'Lower back', 'Shoulders', 'Traps'], array['Begin with a shoulder width, double overhand or hook grip, with the bar hanging at the mid thigh position. Your back should be straight and inclined slightly forward.', 'Begin by aggressively extending through the hips, knees and ankles, driving the weight upward. As you do so, shrug your shoulders towards your ears.', 'Immediately recover by driving through the heels, keeping the torso upright and elbows up. Continue until you have risen to a standing position.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Hang Clean - Below the Knees', 'Quadriceps', 'Barbell', 'Quadriceps', array['Calves', 'Forearms', 'Glutes', 'Hamstrings', 'Lower back', 'Shoulders', 'Traps'], array['Begin with a shoulder width, double overhand or hook grip, with the bar hanging just below the knees. Your back should be straight and inclined slightly forward.', 'Begin by aggressively extending through the hips, knees and ankles, driving the weight upward. As you do so, shrug your shoulders towards your ears. As full extension is achieved, transition into the third pull by aggressively shrugging and flexing the arms with the elbows up and out.', 'At peak extension, aggressively pull yourself down, rotating your elbows under the bar as you do so. Receive the bar in a front squat position, the depth of which is dependent upon the height of the bar at the end of the third pull. The bar should be racked onto the protracted shoulders, lightly touching the throat with the hands relaxed. Continue to descend to the bottom squat position, which will help in the recovery.', 'Immediately recover by driving through the heels, keeping the torso upright and elbows up. Continue until you have risen to a standing position.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Hang Snatch', 'Hamstrings', 'Barbell', 'Hamstrings', array['Abdominals', 'Calves', 'Forearms', 'Glutes', 'Lower back', 'Quadriceps', 'Shoulders', 'Traps'], array['Begin with a wide grip on the bar, with an overhand or hook grip. The feet should be directly below the hips with the feet turned out. Your knees should be slightly bent, and the torso inclined forward. The spine should be fully extended and the head facing forward. The bar should be at the hips. This will be your starting position.', 'Aggressively extend through the legs and hips. At peak extension, shrug the shoulders and allow the elbows to flex to the side.', 'As you move your feet into the receiving position, forcefully pull yourself below the bar as you elevate the bar overhead. Receive the bar with your body as low as possible and the arms fully extended overhead.', 'Return to a standing position with the weight overhead. Follow by returning the weight to the ground under control.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Hang Snatch - Below Knees', 'Hamstrings', 'Barbell', 'Hamstrings', array['Abdominals', 'Calves', 'Forearms', 'Glutes', 'Lower back', 'Quadriceps', 'Shoulders', 'Traps'], array['Begin with a wide grip on the bar, with an overhand or hook grip. The feet should be directly below the hips with the feet turned out. Your knees should be slightly bent, and the torso inclined forward. The spine should be fully extended and the head facing forward. The bar should be just below the knees. This will be your starting position.', 'Aggressively extend through the legs and hips. At peak extension, shrug the shoulders and allow the elbows to flex to the side.', 'As you move your feet into the receiving position, forcefully pull yourself below the bar as you elevate the bar overhead. Receive the bar with your body as low as possible and the arms fully extended overhead.', 'Return to a standing position with the weight overhead, and then return the weight to the floor under control.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Hanging Bar Good Morning', 'Hamstrings', 'Barbell', 'Hamstrings', array['Abdominals', 'Glutes', 'Lower back'], array['Begin with a bar on a rack at about the same height as your stomach. Suspend the bar using chains or suspension straps.', 'Bend over underneath the bar and rack the bar across the rear of your shoulders as you would a power squat, not on top of your traps. At the proper height, you should be near parallel to the floor when bent over. Keep your back tight, shoulder blades pinched together, and your knees slightly bent. Keep your back arched and your cervical spine in proper alignment.', 'Begin the motion by extending through the hips with your glutes and hamstrings, and you are standing with the weight.', 'Slowly lower the weight back to the starting position, where it is supported by the chains.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Hanging Leg Raise', 'Abdominals', 'Bodyweight', 'Abdominals', array[]::text[], array['Hang from a chin-up bar with both arms extended at arms length in top of you using either a wide grip or a medium grip. The legs should be straight down with the pelvis rolled slightly backwards. This will be your starting position.', 'Raise your legs until the torso makes a 90-degree angle with the legs. Exhale as you perform this movement and hold the contraction for a second or so.', 'Go back slowly to the starting position as you breathe in.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Hanging Pike', 'Abdominals', 'Bodyweight', 'Abdominals', array[]::text[], array['Hang from a chin-up bar with your legs and feet together using an overhand grip (palms facing away from you) that is slightly wider than shoulder width. Tip: You may use wrist wraps in order to facilitate holding on to the bar.', 'Now bend your knees at a 90 degree angle and bring the upper legs forward so that the calves are perpendicular to the floor while the thighs remain parallel to it. This will be your starting position.', 'Pull your legs up as you exhale until you almost touch your shins with the bar above you. Tip: Try to straighten your legs as much as possible while at the top.', 'Lower your legs as slowly as possible until you reach the starting position. Tip: Avoid swinging and using momentum at all times.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Heaving Snatch Balance', 'Quadriceps', 'Barbell', 'Quadriceps', array['Abdominals', 'Forearms', 'Glutes', 'Hamstrings', 'Shoulders', 'Triceps'], array['This drill helps you learn the snatch. Begin by holding a light weight across the back of the shoulders. Your feet should be slightly wider than hip width apart with the feet turned out, the same position that you would perform a squat with.', 'Begin by dipping with the knees slightly, and popping back up to briefly unload the bar. Drive yourself underneath the bar, elevating it overhead as you descend into a full squat.', 'Return to a standing position.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Hyperextensions (Back Extensions)', 'Lower back', 'Other', 'Lower back', array['Glutes', 'Hamstrings'], array['Lie face down on a hyperextension bench, tucking your ankles securely under the footpads.', 'Adjust the upper pad if possible so your upper thighs lie flat across the wide pad, leaving enough room for you to bend at the waist without any restriction.', 'With your body straight, cross your arms in front of you (my preference) or behind your head. This will be your starting position. Tip: You can also hold a weight plate for extra resistance in front of you under your crossed arms.', 'Start bending forward slowly at the waist as far as you can while keeping your back flat. Inhale as you perform this movement. Keep moving forward until you feel a nice stretch on the hamstrings and you can no longer keep going without a rounding of the back. Tip: Never round the back as you perform this exercise. Also, some people can go farther than others. The key thing is that you go as far as your body allows you to without rounding the back.', 'Slowly raise your torso back to the initial position as you inhale. Tip: Avoid the temptation to arch your back past a straight line. Also, do not swing the torso at any time in order to protect the back from injury.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Incline Cable Chest Press', 'Chest', 'Cable', 'Chest', array['Shoulders', 'Triceps'], array['Adjust the weight to an appropriate amount and be seated, grasping the handles. Your upper arms should be about 45 degrees to the body, with your head and chest up. The elbows should be bent to about 90 degrees. This will be your starting position.', 'Begin by extending through the elbow, pressing the handles together straight in front of you. Keep your shoulder blades retracted as you execute the movement.', 'After pausing at full extension, return to the starting position, keeping tension on the cables.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Incline Cable Fly', 'Chest', 'Cable', 'Chest', array['Shoulders'], array['To get yourself into the starting position, set the pulleys at the floor level (lowest level possible on the machine that is below your torso).', 'Place an incline bench (set at 45 degrees) in between the pulleys, select a weight on each one and grab a pulley on each hand.', 'With a handle on each hand, lie on the incline bench and bring your hands together at arms length in front of your face. This will be your starting position.', 'With a slight bend of your elbows (in order to prevent stress at the biceps tendon), lower your arms out at both sides in a wide arc until you feel a stretch on your chest. Breathe in as you perform this portion of the movement. Tip: Keep in mind that throughout the movement, the arms should remain stationary. The movement should only occur at the shoulder joint.', 'Return your arms back to the starting position as you squeeze your chest muscles and exhale. Hold the contracted position for a second. Tip: Make sure to use the same arc of motion used to lower the weights.', 'Repeat the movement for the prescribed amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Incline Dumbbell Bench With Palms Facing In', 'Chest', 'Dumbbell', 'Chest', array['Shoulders', 'Triceps'], array['Lie back on an incline bench with a dumbbell on each hand on top of your thighs. The palms of your hand will be facing each other.', 'By using your thighs to help you get the dumbbells up, clean the dumbbells one arm at a time so that you can hold them at shoulder width.', 'Once at shoulder width, keep the palms of your hands with a neutral grip (palms facing each other). Keep your elbows flared out with the upper arms in line with the shoulders (perpendicular to the torso) and the elbows bent creating a 90-degree angle between the upper arm and the forearm. This will be your starting position.', 'Now bring down the weights slowly to your side as you breathe in. Keep full control of the dumbbells at all times.', 'As you breathe out, push the dumbbells up using your pectoral muscles. Lock your arms in the contracted position, hold for a second and then start coming down slowly. Tip: It should take at least twice as long to go down than to come up.', 'Repeat the movement for the prescribed amount of repetitions.', 'When you are done, place the dumbbells back in your thighs and then on the floor. This is the safest manner to dispose of the dumbbells.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Incline Dumbbell Curl', 'Biceps', 'Dumbbell', 'Biceps', array[]::text[], array['Sit back on an incline bench with a dumbbell in each hand held at arms length. Keep your elbows close to your torso and rotate the palms of your hands until they are facing forward. This will be your starting position.', 'While holding the upper arm stationary, curl the weights forward while contracting the biceps as you breathe out. Only the forearms should move. Continue the movement until your biceps are fully contracted and the dumbbells are at shoulder level. Hold the contracted position for a second.', 'Slowly begin to bring the dumbbells back to starting position as your breathe in.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Incline Dumbbell Flys', 'Chest', 'Dumbbell', 'Chest', array['Shoulders'], array['Hold a dumbbell on each hand and lie on an incline bench that is set to an incline angle of no more than 30 degrees.', 'Extend your arms above you with a slight bend at the elbows.', 'Now rotate the wrists so that the palms of your hands are facing you. Tip: The pinky fingers should be next to each other. This will be your starting position.', 'As you breathe in, start to slowly lower the arms to the side while keeping the arms extended and while rotating the wrists until the palms of the hand are facing each other. Tip: At the end of the movement the arms will be by your side with the palms facing the ceiling.', 'As you exhale start to bring the dumbbells back up to the starting position by reversing the motion and rotating the hands so that the pinky fingers are next to each other again. Tip: Keep in mind that the movement will only happen at the shoulder joint and at the wrist. There is no motion that happens at the elbow joint.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Incline Dumbbell Press', 'Chest', 'Dumbbell', 'Chest', array['Shoulders', 'Triceps'], array['Lie back on an incline bench with a dumbbell in each hand atop your thighs. The palms of your hands will be facing each other.', 'Then, using your thighs to help push the dumbbells up, lift the dumbbells one at a time so that you can hold them at shoulder width.', 'Once you have the dumbbells raised to shoulder width, rotate your wrists forward so that the palms of your hands are facing away from you. This will be your starting position.', 'Be sure to keep full control of the dumbbells at all times. Then breathe out and push the dumbbells up with your chest.', 'Lock your arms at the top, hold for a second, and then start slowly lowering the weight. Tip Ideally, lowering the weights should take about twice as long as raising them.', 'Repeat the movement for the prescribed amount of repetitions.', 'When you are done, place the dumbbells back on your thighs and then on the floor. This is the safest manner to release the dumbbells.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Incline Hammer Curls', 'Biceps', 'Dumbbell', 'Biceps', array[]::text[], array['Seat yourself on an incline bench with a dumbbell in each hand. You should pressed firmly against he back with your feet together. Allow the dumbbells to hang straight down at your side, holding them with a neutral grip. This will be your starting position.', 'Initiate the movement by flexing at the elbow, attempting to keep the upper arm stationary.', 'Continue to the top of the movement and pause, then slowly return to the start position.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Incline Push-Up', 'Chest', 'Bodyweight', 'Chest', array['Shoulders', 'Triceps'], array['Stand facing bench or sturdy elevated platform. Place hands on edge of bench or platform, slightly wider than shoulder width.', 'Position forefoot back from bench or platform with arms and body straight. Arms should be perpendicular to body. Keeping body straight, lower chest to edge of box or platform by bending arms.', 'Push body up until arms are extended. Repeat.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Incline Push-Up Close-Grip', 'Triceps', 'Bodyweight', 'Triceps', array['Chest', 'Shoulders'], array['Stand facing a Smith machine bar or sturdy elevated platform at an appropriate height.', 'Place your hands next to one another on the bar.', 'Position your feet back from the bar with arms and body straight. This will be your starting position.', 'Keeping your body straight, lower your chest to the bar by bending the arms.', 'Return to the starting position by extending the elbows, pressing yourself back up.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Internal Rotation with Band', 'Shoulders', 'Other', 'Shoulders', array[]::text[], array['Choke the band around a post. The band should be at the same height as your elbow. Stand with your right side to the band a couple of feet away.', 'Grasp the end of the band with your right hand, and keep your elbow pressed firmly to your side. We recommend you hold a pad or foam roll in place with your elbow to keep it firmly in position.', 'With your upper arm in position, your elbow should be flexed to 90 degrees with your hand reaching away from your torso. This will be your starting position.', 'Execute the movement by rotating your arm in a forehand motion, keeping your elbow in place.', 'Continue as far as you are able, pause, and then return to the starting position.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Inverted Row', 'Middle back', null, 'Middle back', array['Lats'], array['Position a bar in a rack to about waist height. You can also use a smith machine.', 'Take a wider than shoulder width grip on the bar and position yourself hanging underneath the bar. Your body should be straight with your heels on the ground with your arms fully extended. This will be your starting position.', 'Begin by flexing the elbow, pulling your chest towards the bar. Retract your shoulder blades as you perform the movement.', 'Pause at the top of the motion, and return yourself to the start position.', 'Repeat for the desired number of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Jackknife Sit-Up', 'Abdominals', 'Bodyweight', 'Abdominals', array[]::text[], array['Lie flat on the floor (or exercise mat) on your back with your arms extended straight back behind your head and your legs extended also. This will be your starting position.', 'As you exhale, bend at the waist while simultaneously raising your legs and arms to meet in a jackknife position. Tip: The legs should be extended and lifted at approximately a 35-45 degree angle from the floor and the arms should be extended and parallel to your legs. The upper torso should be off the floor.', 'While inhaling, lower your arms and legs back to the starting position.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Jerk Balance', 'Shoulders', 'Barbell', 'Shoulders', array['Glutes', 'Hamstrings', 'Quadriceps', 'Triceps'], array['This drill helps you learn to drive yourself low enough during the jerk and corrects those who move backward during the movement. Begin with the bar racked in the jerk position, with the shoulders forward, torso upright, and the feet split slightly apart.', 'Initiate the movement as you would a normal jerk, dipping at the knees while keeping your torso vertical, and driving back up forcefully, using momentum and not your arms to elevate the weight.', 'Keep the rear foot in place, using it to drive your body forward into a full split as you jerk the weight. Recover by standing up with the weight overhead.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Jerk Dip Squat', 'Quadriceps', 'Barbell', 'Quadriceps', array['Abdominals', 'Calves'], array['This movement strengthens the dip portion of the jerk. Begin with the bar racked in the jerk position, with the shoulders forward to create a shelf and the bar lightly contacting the throat. The feet should be directly under the hips, with the feet turned out as is comfortable.', 'Keeping the torso vertical, dip by flexing the knees, allowing them to travel forward and without moving the hips to the rear. The dip should not be excessive. Return the weight to the starting position by driving forcefully though the feet.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Kettlebell One-Legged Deadlift', 'Hamstrings', 'Kettlebells', 'Hamstrings', array['Glutes', 'Lower back'], array['Hold a kettlebell by the handle in one hand. Stand on one leg, on the same side that you hold the kettlebell.', 'Keeping that knee slightly bent, perform a stiff legged deadlift by bending at the hip, extending your free leg behind you for balance.', 'Continue lowering the kettlebell until you are parallel to the ground, and then return to the upright position.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Kettlebell Pistol Squat', 'Quadriceps', 'Kettlebells', 'Quadriceps', array['Calves', 'Glutes', 'Hamstrings', 'Shoulders'], array['Pick up a kettlebell with two hands and hold it by the horns. Hold one leg off of the floor and squat down on the other.', 'Squat down by flexing the knee and sitting back with the hips, holding the kettlebell up in front of you.', 'Hold the bottom position for a second and then reverse the motion, driving through the heel and keeping your head and chest up.', 'Lower yourself again and repeat.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Kipping Muscle Up', 'Lats', 'Other', 'Lats', array['Abdominals', 'Biceps', 'Forearms', 'Middle back', 'Shoulders', 'Traps', 'Triceps'], array['Grip the rings using a false grip, with the base of your palms on top of the rings.', 'Begin with a movement swinging your legs backward slightly.', 'Counter that movement by swinging your legs forward and up, jerking your chin and chest back, pulling yourself up with both arms as you do so. As you reach the top position of the pull-up, pull the rings to your armpits as you roll your shoulders forward, allowing your elbows to move straight back behind you. This puts you into the proper position to continue into the dip portion of the movement.', 'Maintaining control and stability, extend through the elbow to complete the motion.', 'Use care when lowering yourself to the ground.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Kneeling High Pulley Row', 'Lats', 'Cable', 'Lats', array['Biceps', 'Middle back'], array['Select the appropriate weight using a pulley that is above your head. Attach a rope to the cable and kneel a couple of feet away, holding the rope out in front of you with both arms extended. This will be your starting position.', 'Initiate the movement by flexing the elbows and fully retracting your shoulders, pulling the rope toward your upper chest with your elbows out.', 'After pausing briefly, slowly return to the starting position.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Landmine 180''s', 'Abdominals', 'Barbell', 'Abdominals', array['Glutes', 'Lower back', 'Shoulders'], array['Position a bar into a landmine or securely anchor it in a corner. Load the bar to an appropriate weight.', 'Raise the bar from the floor, taking it to shoulder height with both hands with your arms extended in front of you. Adopt a wide stance. This will be your starting position.', 'Perform the movement by rotating the trunk and hips as you swing the weight all the way down to one side. Keep your arms extended throughout the exercise.', 'Reverse the motion to swing the weight all the way to the opposite side.', 'Continue alternating the movement until the set is complete.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Leg Extensions', 'Quadriceps', 'Machine', 'Quadriceps', array[]::text[], array['For this exercise you will need to use a leg extension machine. First choose your weight and sit on the machine with your legs under the pad (feet pointed forward) and the hands holding the side bars. This will be your starting position. Tip: You will need to adjust the pad so that it falls on top of your lower leg (just above your feet). Also, make sure that your legs form a 90-degree angle between the lower and upper leg. If the angle is less than 90-degrees then that means the knee is over the toes which in turn creates undue stress at the knee joint. If the machine is designed that way, either look for another machine or just make sure that when you start executing the exercise you stop going down once you hit the 90-degree angle.', 'Using your quadriceps, extend your legs to the maximum as you exhale. Ensure that the rest of the body remains stationary on the seat. Pause a second on the contracted position.', 'Slowly lower the weight back to the original position as you inhale, ensuring that you do not go past the 90-degree angle limit.', 'Repeat for the recommended amount of times.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Leg Press', 'Quadriceps', 'Machine', 'Quadriceps', array['Calves', 'Glutes', 'Hamstrings'], array['Using a leg press machine, sit down on the machine and place your legs on the platform directly in front of you at a medium (shoulder width) foot stance. (Note: For the purposes of this discussion we will use the medium stance described above which targets overall development; however you can choose any of the three stances described in the foot positioning section).', 'Lower the safety bars holding the weighted platform in place and press the platform all the way up until your legs are fully extended in front of you. Tip: Make sure that you do not lock your knees. Your torso and the legs should make a perfect 90-degree angle. This will be your starting position.', 'As you inhale, slowly lower the platform until your upper and lower legs make a 90-degree angle.', 'Pushing mainly with the heels of your feet and using the quadriceps go back to the starting position as you exhale.', 'Repeat for the recommended amount of repetitions and ensure to lock the safety pins properly once you are done. You do not want that platform falling on you fully loaded.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Low Cable Crossover', 'Chest', 'Cable', 'Chest', array['Shoulders'], array['To move into the starting position, place the pulleys at the low position, select the resistance to be used and grasp a handle in each hand.', 'Step forward, gaining tension in the pulleys. Your palms should be facing forward, hands below the waist, and your arms straight. This will be your starting position.', 'With a slight bend in your arms, draw your hands upward and toward the midline of your body. Your hands should come together in front of your chest, palms facing up.', 'Return your arms back to the starting position after a brief pause.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Lying Dumbbell Tricep Extension', 'Triceps', 'Dumbbell', 'Triceps', array['Chest', 'Shoulders'], array['Lie on a flat bench while holding two dumbbells directly in front of you. Your arms should be fully extended at a 90-degree angle from your torso and the floor. The palms should be facing in and the elbows should be tucked in. This is the starting position.', 'As you breathe in and you keep the upper arms stationary with the elbows in, slowly lower the weight until the dumbbells are near your ears.', 'At that point, while keeping the elbows in and the upper arms stationary, use the triceps to bring the weight back up to the starting position as you breathe out.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Lying Leg Curls', 'Hamstrings', 'Machine', 'Hamstrings', array[]::text[], array['Adjust the machine lever to fit your height and lie face down on the leg curl machine with the pad of the lever on the back of your legs (just a few inches under the calves). Tip: Preferably use a leg curl machine that is angled as opposed to flat since an angled position is more favorable for hamstrings recruitment.', 'Keeping the torso flat on the bench, ensure your legs are fully stretched and grab the side handles of the machine. Position your toes straight (or you can also use any of the other two stances described on the foot positioning section). This will be your starting position.', 'As you exhale, curl your legs up as far as possible without lifting the upper legs from the pad. Once you hit the fully contracted position, hold it for a second.', 'As you inhale, bring the legs back to the initial position. Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Lying Machine Squat', 'Quadriceps', 'Machine', 'Quadriceps', array['Calves', 'Glutes', 'Hamstrings'], array['Adjust the leg machine to a height that will allow you to get inside it with your knees bent and the thighs slightly below parallel.', 'Once you select the weight, position yourself inside the machine face up with the knees bent and thighs slightly below parallel to the platform. Make sure that the knees do not go past the toes. The angle created between the hamstrings and the calves should be one that is slightly less than 90 degrees (since your starting position requires you to start slightly below parallel). Your back and your head should be resting on the machine while your shoulders are pressed under the shoulder pads.', 'Place your hands by the handles and position your feet slightly pointing out at a shoulder width position. This will be your starting position.', 'While pressing with the balls of the feet as you breathe out, make your whole body erect as you squeeze the quads. Hold the contracted position for a second. Tip: Since you are starting below parallel, you can opt to use your hands to help you up by pressing on your thighs only on the first repetition.', 'Slowly squat down as you inhale but instead of going all the way down to the starting position, just stop once your thighs are parallel to the platform. The angle between your hamstrings and calves should be a 90-degree angle.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Lying Rear Delt Raise', 'Shoulders', 'Dumbbell', 'Shoulders', array[]::text[], array['While holding a dumbbell in each hand, lay with your chest down on a flat bench.', 'Position the palms of the hands in a neutral manner (palms facing your torso) as you keep the arms extended with the elbows slightly bent. This will be your starting position.', 'Now raise the arms to the side until your elbows are at shoulder height and your arms are roughly parallel to the floor as you exhale. Tip: Maintain your arms perpendicular to the torso while keeping them extended throughout the movement. Also, keep the contraction at the top for a second.', 'Slowly lower the dumbbells to the starting position as you inhale.', 'Repeat for the recommended amount of repetitions and then switch to the other arm.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Lying T-Bar Row', 'Middle back', 'Machine', 'Middle back', array['Biceps', 'Lats'], array['Load up the T-bar Row Machine with the desired weight and adjust the leg height so that your upper chest is at the top of the pad. Tip: In some machines all you can do is stand on the appropriate step that allows you to be at a height that has the upper chest at the top of the pad.', 'Lay face down on the pad and grab the handles. You can either use a palms down, palms up, or palms in position depending on what part of your back you want to emphasize.', 'Lift the bar off the rack and extend your arms in front of you. This will be your starting position.', 'As you exhale slowly pull the weight up and squeeze your back at the top of the movement. Tip: Keep the upper arms as close to the torso as possible throughout the movement in order to better engage the back muscles. Also, do not lift your body off of the pad at any time and refrain from using the biceps to lift the weight.', 'After a second contraction at the top of the movement, as you inhale, slowly go back down to the starting position.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Machine Bench Press', 'Chest', 'Machine', 'Chest', array['Shoulders', 'Triceps'], array['Sit down on the Chest Press Machine and select the weight.', 'Step on the lever provided by the machine since it will help you to bring the handles forward so that you can grab the handles and fully extend the arms.', 'Grab the handles with a palms-down grip and lift your elbows so that your upper arms are parallel to the floor to the sides of your torso. Tip: Your forearms will be pointing forward since you are grabbing the handles. Once you bring the handles forward and extend the arms you will be at the starting position.', 'Now bring the handles back towards you as you breathe in.', 'Push the handles away from you as you flex your pecs and you breathe out. Hold the contraction for a second before going back to the starting position.', 'Repeat for the recommended amount of reps.', 'When finished step on the lever again and slowly get the handles back to their original place.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Machine Bicep Curl', 'Biceps', 'Machine', 'Biceps', array[]::text[], array['Adjust the seat to the appropriate height and make your weight selection. Place your upper arms against the pads and grasp the handles. This will be your starting position.', 'Perform the movement by flexing the elbow, pulling your lower arm towards your upper arm.', 'Pause at the top of the movement, and then slowly return the weight to the starting position.', 'Avoid returning the weight all the way to the stops until the set is complete to keep tension on the muscles being worked.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Machine Preacher Curls', 'Biceps', 'Machine', 'Biceps', array[]::text[], array['Sit down on the Preacher Curl Machine and select the weight.', 'Place the back of your upper arms (your triceps) on the preacher pad provided and grab the handles using an underhand grip (palms facing up). Tip: Make sure that when you place the arms on the pad you keep the elbows in. This will be your starting position.', 'Now lift the handles as you exhale and you contract the biceps. At the top of the position make sure that you hold the contraction for a second. Tip: Only the forearms should move. The upper arms should remain stationary and on the pad at all times.', 'Lower the handles slowly back to the starting position as you inhale.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Machine Shoulder (Military) Press', 'Shoulders', 'Machine', 'Shoulders', array['Triceps'], array['Sit down on the Shoulder Press Machine and select the weight.', 'Grab the handles to your sides as you keep the elbows bent and in line with your torso. This will be your starting position.', 'Now lift the handles as you exhale and you extend the arms fully. At the top of the position make sure that you hold the contraction for a second.', 'Lower the handles slowly back to the starting position as you inhale.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Machine Triceps Extension', 'Triceps', 'Machine', 'Triceps', array[]::text[], array['Adjust the seat to the appropriate height and make your weight selection. Place your upper arms against the pads and grasp the handles. This will be your starting position.', 'Perform the movement by extending the elbow, pulling your lower arm away from your upper arm.', 'Pause at the completion of the movement, and then slowly return the weight to the starting position.', 'Avoid returning the weight all the way to the stops until the set is complete to keep tension on the muscles being worked.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Muscle Snatch', 'Hamstrings', 'Barbell', 'Hamstrings', array['Glutes', 'Lower back', 'Quadriceps', 'Shoulders', 'Triceps'], array['Begin with a loaded barbell held at the mid thigh position with a wide grip. The feet should be directly below the hips, with the feet turned out as needed. Lower the hips, with the chest up and the head looking forward. The shoulders should be just in front of the bar. This will be the starting position.', 'Begin the pull by driving through the front of the heels, raising the bar. Transition into the second pull by extending through the hips knees and ankles, driving the bar up as quickly as possible. The bar should be close to the body.', 'Continue raising the bar to the overhead position, without rebending the knees.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Muscle Up', 'Lats', 'Other', 'Lats', array['Abdominals', 'Biceps', 'Forearms', 'Middle back', 'Shoulders', 'Traps', 'Triceps'], array['Grip the rings using a false grip, with the base of your palms on top of the rings. Initiate a pull up by pulling the elbows down to your side, flexing the elbows.', 'As you reach the top position of the pull-up, pull the rings to your armpits as you roll your shoulders forward, allowing your elbows to move straight back behind you. This puts you into the proper position to continue into the dip portion of the movement.', 'Maintaining control and stability, extend through the elbow to complete the motion.', 'Use care when lowering yourself to the ground.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Natural Glute Ham Raise', 'Hamstrings', 'Bodyweight', 'Hamstrings', array['Calves', 'Glutes', 'Lower back'], array['Using the leg pad of a lat pulldown machine or a preacher bench, position yourself so that your ankles are under the pads, knees on the seat, and you are facing away from the machine. You should be upright and maintaining good posture.', 'This will be your starting position. Lower yourself under control until your knees are almost completely straight.', 'Remaining in control, raise yourself back up to the starting position.', 'If you are unable to complete a rep, use a band, a partner, or push off of a box to aid in completing a repetition.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Oblique Crunches', 'Abdominals', 'Bodyweight', 'Abdominals', array[]::text[], array['Lie flat on the floor with your lower back pressed to the ground. For this exercise, you will need to put one hand beside your head and the other to the side against the floor.', 'Make sure your feet are elevated and resting on a flat surface.', 'Now lift the shoulder in which your hand is touching your head.', 'Simply elevate your shoulder and body upward until you touch your knee. For example, if you have your right hand besides your head, then you want to elevate your body upwards until your right elbow touches your left knee. The same variation can be applied doing the inverse and using your left elbow to touch your right knee.', 'After your knee touches your elbow, lower your body until you have reached the starting position.', 'Remember to breathe in during the eccentric (lowering) part of the exercise and to breathe out during the concentric (upward) part of the exercise.', 'Continue alternating in this manner until all of the recommended repetitions for each side have been completed.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Olympic Squat', 'Quadriceps', 'Barbell', 'Quadriceps', array['Calves', 'Glutes', 'Hamstrings'], array['Begin with a barbell supported on top of the traps. The chest should be up, and the head facing forward. Adopt a hip width stance with the feet turned out as needed.', 'Descend by flexing the knees, refraining from moving the hips back as much as possible. This requires that the knees travel forward; ensure that they stay aligned with the feet. The goal is to keep the torso as upright as possible. Continue all the way down, keeping the weight on the front of the heel.', 'At the moment the upper legs contact the lower, reverse the motion, driving the weight upward.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('One-Arm Dumbbell Row', 'Middle back', 'Dumbbell', 'Middle back', array['Biceps', 'Lats', 'Shoulders'], array['Choose a flat bench and place a dumbbell on each side of it.', 'Place the right leg on top of the end of the bench, bend your torso forward from the waist until your upper body is parallel to the floor, and place your right hand on the other end of the bench for support.', 'Use the left hand to pick up the dumbbell on the floor and hold the weight while keeping your lower back straight. The palm of the hand should be facing your torso. This will be your starting position.', 'Pull the resistance straight up to the side of your chest, keeping your upper arm close to your side and keeping the torso stationary. Breathe out as you perform this step. Tip: Concentrate on squeezing the back muscles once you reach the full contracted position. Also, make sure that the force is performed with the back muscles and not the arms. Finally, the upper torso should remain stationary and only the arms should move. The forearms should do no other work except for holding the dumbbell; therefore do not try to pull the dumbbell up using the forearms.', 'Lower the resistance straight down to the starting position. Breathe in as you perform this step.', 'Repeat the movement for the specified amount of repetitions.', 'Switch sides and repeat again with the other arm.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('One-Arm Long Bar Row', 'Middle back', 'Barbell', 'Middle back', array['Biceps', 'Lats'], array['Position a bar into a landmine or in a corner to keep it from moving. Load an appropriate weight onto your end.', 'Stand next to the bar, and take a grip with one hand close to the collar. Using your hips and legs, rise to a standing position.', 'Assume a bent-knee stance with your hips back and your chest up. Your arm should be extended. This will be your starting position.', 'Pull the weight to your side by retracting the shoulder and flexing the elbow. Do not jerk the weight or cheat during the movement.', 'After a brief pause, return to the starting position.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('One-Arm Medicine Ball Slam', 'Abdominals', 'Other', 'Abdominals', array['Lats', 'Shoulders'], array['Start in a standing position with a staggered, athletic stance. Hold a medicine ball in one hand, on the same side as your back leg. This will be your starting position.', 'Begin by winding the arm, raising the medicine ball above your head. As you do so, extend through the hips, knees, and ankles to load up for the slam.', 'At peak extension, flex the shoulders, spine, and hips to throw the ball hard into the ground directly in front of you.', 'Catch the ball on the bounce and continue for the desired number of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('One-Legged Cable Kickback', 'Glutes', 'Cable', 'Glutes', array['Hamstrings'], array['Hook a leather ankle cuff to a low cable pulley and then attach the cuff to your ankle.', 'Face the weight stack from a distance of about two feet, grasping the steel frame for support.', 'While keeping your knees and hips bent slightly and your abs tight, contract your glutes to slowly "kick" the working leg back in a semicircular arc as high as it will comfortably go as you breathe out. Tip: At full extension, squeeze your glutes for a second in order to achieve a peak contraction.', 'Now slowly bring your working leg forward, resisting the pull of the cable until you reach the starting position.', 'Repeat for the recommended amount of repetitions.', 'Switch legs and repeat the movement for the other side.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('One Arm Chin-Up', 'Middle back', 'Other', 'Middle back', array['Biceps', 'Forearms', 'Lats'], array['For this exercise, start out by placing a towel around a chin up bar.', 'Grab the chin-up bar with your palm facing you. One hand will be grabbing the chin-up bar and the other will be grabbing the towel.', 'Bring your torso back around 30 degrees or so while creating a curvature on your lower back and sticking your chest out. This is your starting position.v', 'Pull your torso up until the bar touches your upper chest by drawing the shoulders and the upper arms down and back. Exhale as you perform this portion of the movement. Tip: Concentrate on squeezing the back muscles once you reach the full contracted position. The upper torso should remain stationary as it moves through space and only the arms should move. The forearms should do no other work other than hold the bar.', 'After a second on the contracted position, start to inhale and slowly lower your torso back to the starting position when your arms are fully extended and the lats are fully stretched.', 'Repeat this motion for the prescribed amount of repetitions.', 'Switch arms and repeat the movement.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('One Arm Dumbbell Preacher Curl', 'Biceps', 'Dumbbell', 'Biceps', array[]::text[], array['Grab a dumbbell with the right arm and place the upper arm on top of the preacher bench or the incline bench. The dumbbell should be held at shoulder length. This will be your starting position.', 'As you breathe in, slowly lower the dumbbell until your upper arm is extended and the biceps is fully stretched.', 'As you exhale, use the biceps to curl the weight up until your biceps is fully contracted and the dumbbell is at shoulder height. Again, remember that to ensure full contraction you need to bring that small finger higher than the thumb.', 'Squeeze the biceps hard for a second at the contracted position and repeat for the recommended amount of repetitions.', 'Switch arms and repeat the movement.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('One Arm Lat Pulldown', 'Lats', 'Cable', 'Lats', array['Biceps', 'Middle back'], array['Select an appropriate weight and adjust the knee pad to help keep you down. Grasp the handle with a pronated grip. This will be your starting position.', 'Pull the handle down, squeezing your elbow to your side as you flex the elbow.', 'Pause at the bottom of the motion, and then slowly return the handle to the starting position.', 'For multiple repetitions, avoid completely returning the weight to keep tension on the muscles being worked.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('One Leg Barbell Squat', 'Quadriceps', 'Barbell', 'Quadriceps', array['Calves', 'Glutes', 'Hamstrings'], array['Start by standing about 2 to 3 feet in front of a flat bench with your back facing the bench. Have a barbell in front of you on the floor. Tip: Your feet should be shoulder width apart from each other.', 'Bend the knees and use a pronated grip with your hands being wider than shoulder width apart from each other to lift the barbell up until you can rest it on your chest.', 'Then lift the barbell over your head and rest it on the base of your neck. Move one foot back so that your toe is resting on the flat bench. Your other foot should be stationary in front of you. Keep your head up at all times as looking down will get you off balance and also maintain a straight back. Tip: Make sure your back is straight and chest is out while performing this exercise.', 'As you inhale, slowly lower your leg until your thigh is parallel to the floor. At this point, your knee should be over your toes. Your chest should be directly above the middle of your thigh.', 'Leading with the chest and hips and contracting the quadriceps, elevate your leg back to the starting position as you exhale.', 'Repeat for the recommended amount of repetitions.', 'Switch legs and repeat the movement.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Pallof Press', 'Abdominals', 'Cable', 'Abdominals', array['Chest', 'Shoulders', 'Triceps'], array['Connect a standard handle to a tower, andif possibleposition the cable to shoulder height. If not, a low pulley will suffice.', 'With your side to the cable, grab the handle with both hands and step away from the tower. You should be approximately arm''s length away from the pulley, with the tension of the weight on the cable.', 'With your feet positioned hip-width apart and knees slightly bent, hold the cable to the middle of your chest. This will be your starting position.', 'Press the cable away from your chest, fully extending both arms. You core should be tight and engaged.', 'Hold the repetition for several seconds before returning to the starting position.', 'At the conclusion of the set, repeat facing the other direction.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Palms-Down Dumbbell Wrist Curl Over A Bench', 'Forearms', 'Dumbbell', 'Forearms', array[]::text[], array['Start out by placing two dumbbells on one side of a flat bench.', 'Kneel down on both of your knees so that your body is facing the flat bench.', 'Use your arms to grab both of the dumbbells with a pronated grip (palms facing down) and bring them up so that your forearms are resting against the flat bench. Your wrists should be hanging over the edge.', 'Start out by curling your wrist upwards and exhaling.', 'Slowly lower your wrists back down to the starting position while inhaling.', 'Your forearms should be stationary as your wrist is the only movement needed to perform this exercise.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Palms-Up Dumbbell Wrist Curl Over A Bench', 'Forearms', 'Dumbbell', 'Forearms', array[]::text[], array['Start out by placing two dumbbells on one side of a flat bench.', 'Kneel down on both of your knees so that your body is facing the flat bench.', 'Use your arms to grab both of the dumbbells with a supinated grip (palms up) and bring them up so that your forearms are resting against the flat bench. Your wrists should be hanging over the edge.', 'Start out by curling your wrist upwards and exhaling.', 'Slowly lower your wrists back down to the starting position while inhaling. Make sure to inhale during this part of the exercise.', 'Your forearms should be stationary as your wrist is the only movement needed to perform this exercise.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Parallel Bar Dip', 'Triceps', 'Other', 'Triceps', array['Chest', 'Shoulders'], array['Stand between a set of parallel bars. Place a hand on each bar, and then take a small jump to help you get into the starting position with your arms locked out.', 'Begin by flexing the elbow, lowering your body until your arms break 90 degrees. Avoid swinging, and maintain good posture throughout the descent.', 'Reverse the motion by extending the elbow, pushing yourself back up into the starting position.', 'Repeat for the desired number of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Plank', 'Abdominals', 'Bodyweight', 'Abdominals', array[]::text[], array['Get into a prone position on the floor, supporting your weight on your toes and your forearms. Your arms are bent and directly below the shoulder.', 'Keep your body straight at all times, and hold this position as long as possible. To increase difficulty, an arm or leg can be raised.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Power Clean', 'Hamstrings', 'Barbell', 'Hamstrings', array['Calves', 'Forearms', 'Glutes', 'Lower back', 'Middle back', 'Quadriceps', 'Shoulders', 'Traps', 'Triceps'], array['Stand with your feet slightly wider than shoulder width apart and toes pointing out slightly.', 'Squat down and grasp bar with a closed, pronated grip. Your hands should be slightly wider than shoulder width apart outside knees with elbows fully extended.', 'Place the bar about 1 inch in front of your shins and over the balls of your feet.', 'Your back should be flat or slightly arched, your chest held up and out and your shoulder blades should be retracted.', 'Keep your head in a neutral position (in line with vertebral column and not tilted or rotated) with your eyes focused straight ahead. Inhale during this phase.', 'Lift the bar from the floor by forcefully extending the hips and the knees as you exhale. Tip: The upper torso should maintain the same angle. Do not bend at the waist yet and do not let the hips rise before the shoulders (this would have the effect of pushing the glutes in the air and stretching the hamstrings.', 'Keep elbows fully extended with the head in a neutral position and the shoulders over the bar.', 'As the bar raises keep it as close to the shins as possible.', 'As the bar passes the knees, thrust your hips forward and slightly bend the knees to avoid locking them. Tip: At this point your thighs should be against the bar.', 'Keep the back flat or slightly arched, elbows fully extended and your head neutral. Tip: You will hold your breath until the next phase.', 'Inhale and then forcefully and quickly extend your hips and knees and stand on your toes.', 'Keep the bar as close to your body as possible. Tip: Your back should be flat with the elbows pointed out to the sides and your head in a neutral position. Also, keep your shoulders over the bar and arms straight as long as possible.', 'When your lower body joints are fully extended, shrug the shoulders upward rapidly without letting the elbows flex yet. Exhale during this portion of the movement.', 'As the shoulders reach their highest elevation flex your elbows to begin pulling your body under the bar.', 'Continue to pull the arms as high and as long as possible. Tip: Due to the explosive nature of this phase, your torso will be erect or with an arched back, your head will be tilted back slightly and your feet may lose contact with the floor.', 'After the lower body has fully extended and the bar reaches near maximal height, pull your body under the bar and rotate the arms around and under the bar.', 'Simultaneously, flex the hips and knees into a quarter squat position.', 'Once the arms are under the bar, inhale and then lift your elbows to position the upper arms parallel to the floor. Rack the bar across the front of your collar bones and front shoulder muscles.', 'Catch the bar with an erect and tight torso, a neutral head position and flat feet. Exhale during this movement.', 'Stand up by extending the hips and knees to a fully erect position.', 'Lower the bar by gradually reducing the muscular tension of the arms to allow a controlled descent of the bar to the thighs. Inhale during this movement.', 'Simultaneously flex the hips and knees to cushion the impact of the bar on the thighs.', 'Squat down with the elbows fully extended until the bar touches the floor.', 'Start over at Phase 1 and repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Power Clean from Blocks', 'Hamstrings', 'Barbell', 'Hamstrings', array['Quadriceps'], array['With a barbell on boxes of the desired height, take a grip just outside the legs. Lower your hips with the weight focused on the heels, back straight, head facing forward, chest up, with your shoulders just in front of the bar. This will be your starting position.', 'Begin the first pull by driving through the heels, extending your knees. Your back angle should stay the same, and your arms should remain straight. As the bar approaches the mid-thigh position, begin extending through the hips.', 'In a jumping motion, accelerate by extending the hips, knees, and ankles, using speed to move the bar upward. There should be no need to actively pull through the arms to accelerate the weight. At the end of the second pull, the body should be fully extended, leaning slightly back, with the arms still extended.', 'As full extension is achieved, transition into the third pull by aggressively shrugging and flexing the arms with the elbows up and out. At peak extension, pull yourself under the bar far enough that it can be racked onto the shoulders, rotating your elbows under the bar as you do so. The bar should be racked onto the protracted shoulders, lightly touching the throat with the hands relaxed.', 'Immediately recover by driving through the heels, keeping the torso upright and elbows up. Continue until you have risen to a standing position, and complete the repetition by returning the weight to the boxes.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Power Jerk', 'Quadriceps', 'Barbell', 'Quadriceps', array['Abdominals', 'Calves', 'Glutes', 'Hamstrings', 'Shoulders', 'Triceps'], array['Standing with the weight racked on the front of the shoulders, begin with the dip. With your feet directly under your hips, flex the knees without moving the hips backward. Go down only slightly, and reverse direction as powerfully as possible.', 'Drive through the heels create as much speed and force as possible, and be sure to move your head out of the way as the bar leaves the shoulders.', 'At this moment as the feet leave the floor, the feet must be placed into the receiving position as quickly as possible. In the brief moment the feet are not actively driving against the platform, the athletes effort to push the bar up will drive them down. The feet should be moved to a slightly wider stance, with the knees partially bent.', 'Receive the bar with the arms locked out overhead.', 'Return to a standing position.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Power Partials', 'Shoulders', 'Dumbbell', 'Shoulders', array[]::text[], array['Stand up with your torso upright and a dumbbell on each hand being held at arms length. The elbows should be close to the torso.', 'The palms of the hands should be facing your torso. Your feet should be about shoulder width apart. This will be your starting position.', 'Keeping your arms straight and the torso stationary, lift the weights out to your sides until they are about shoulder level height while exhaling.', 'Feel the contraction for a second and begin to lower the weights back down to the starting position while inhaling. Tip: Keep the palms facing down with the little finger slightly higher while lifting and lowering the weights as it will concentrate the stress on your shoulders mainly.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Power Snatch', 'Hamstrings', 'Barbell', 'Hamstrings', array['Calves', 'Glutes', 'Lower back', 'Quadriceps', 'Shoulders', 'Traps', 'Triceps'], array['Begin with a loaded barbell on the floor. The bar should be close to or touching the shins, and a wide grip should be taken on the bar. The feet should be directly below the hips, with the feet turned out as needed. Lower the hips, with the chest up and the head looking forward. The shoulders should be just in front of the bar. This will be the starting position.', 'Begin the first pull by driving through the front of the heels, raising the bar from the ground. The back angle should stay the same until the bar passes the knees.', 'Transition into the second pull by extending through the hips knees and ankles, driving the bar up as quickly as possible. The bar should be close to the body. At peak extension, shrug the shoulders and allow the elbows to flex to the side.', 'As you move your feet into the receiving position, a slightly wider position, pull yourself below the bar as you elevate the bar overhead. The bar should be received in a partial squat. Continue raising the bar to the overhead position, receiving the bar locked out overhead.', 'Return to a standing position with the weight over head.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Power Snatch from Blocks', 'Quadriceps', 'Barbell', 'Quadriceps', array['Calves', 'Forearms', 'Glutes', 'Hamstrings', 'Lower back', 'Shoulders', 'Traps', 'Triceps'], array['Begin with a loaded barbell on boxes or stands of the desired height. A wide grip should be taken on the bar. The feet should be directly below the hips, with the feet turned out as needed. Lower the hips, with the chest up and the head looking forward. The shoulders should be just in front of the bar, with the elbows pointed out. This will be the starting position.', 'Begin the first pull by driving through the front of the heels, raising the bar from the boxes.', 'Transition into the second pull by extending through the hips knees and ankles, driving the bar up as quickly as possible. The bar should be close to the body. At peak extension, shrug the shoulders and allow the elbows to flex to the side.', 'As you move your feet into the receiving position, forcefully pull yourself below the bar as you elevate the bar overhead. The feet should move to just outside the hips, turned out as necessary. Receive the bar above a full squat and with the arms fully extended overhead.', 'Keeping the bar aligned over the front of the heels, your head and chest up, drive through heels of the feet to move to a standing position. Carefully return the weight to the boxes.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Preacher Curl', 'Biceps', 'Barbell', 'Biceps', array[]::text[], array['To perform this movement you will need a preacher bench and an E-Z bar. Grab the E-Z curl bar at the close inner handle (either have someone hand you the bar which is preferable or grab the bar from the front bar rest provided by most preacher benches). The palm of your hands should be facing forward and they should be slightly tilted inwards due to the shape of the bar.', 'With the upper arms positioned against the preacher bench pad and the chest against it, hold the E-Z Curl Bar at shoulder length. This will be your starting position.', 'As you breathe in, slowly lower the bar until your upper arm is extended and the biceps is fully stretched.', 'As you exhale, use the biceps to curl the weight up until your biceps is fully contracted and the bar is at shoulder height. Squeeze the biceps hard and hold this position for a second.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Preacher Hammer Dumbbell Curl', 'Biceps', 'Dumbbell', 'Biceps', array['Forearms'], array['Place the upper part of both arms on top of the preacher bench as you hold a dumbbell in each hand with the palms facing each other (neutral grip).', 'As you breathe in, slowly lower the dumbbells until your upper arm is extended and the biceps is fully stretched.', 'As you exhale, use the biceps to curl the weight up until your biceps is fully contracted and the dumbbells are at shoulder height.', 'Squeeze the biceps hard for a second at the contracted position and repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Press Sit-Up', 'Abdominals', 'Barbell', 'Abdominals', array['Chest', 'Shoulders', 'Triceps'], array['To begin, lie down on a bench with a barbell resting on your chest. Position your legs so they are secure on the extension of the abdominal bench. This is the starting position.', 'While inhaling, tighten your abdominals and glutes. Simultaneously curl your torso as you do when performing a sit-up and press the barbell to an overhead position while exhaling. Tip: Use your arms to push the barbell out as you perform this exercise while still focusing on the abdominal muscles.', 'Lower your upper body back down to the starting position while bringing the barbell back down to your torso. Remember to breathe in while lowering the body.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Pullups', 'Lats', 'Bodyweight', 'Lats', array['Biceps', 'Middle back'], array['Grab the pull-up bar with the palms facing forward using the prescribed grip. Note on grips: For a wide grip, your hands need to be spaced out at a distance wider than your shoulder width. For a medium grip, your hands need to be spaced out at a distance equal to your shoulder width and for a close grip at a distance smaller than your shoulder width.', 'As you have both arms extended in front of you holding the bar at the chosen grip width, bring your torso back around 30 degrees or so while creating a curvature on your lower back and sticking your chest out. This is your starting position.', 'Pull your torso up until the bar touches your upper chest by drawing the shoulders and the upper arms down and back. Exhale as you perform this portion of the movement. Tip: Concentrate on squeezing the back muscles once you reach the full contracted position. The upper torso should remain stationary as it moves through space and only the arms should move. The forearms should do no other work other than hold the bar.', 'After a second on the contracted position, start to inhale and slowly lower your torso back to the starting position when your arms are fully extended and the lats are fully stretched.', 'Repeat this motion for the prescribed amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Push-Up Wide', 'Chest', 'Bodyweight', 'Chest', array['Abdominals', 'Shoulders', 'Triceps'], array['With your hands wide apart, support your body on your toes and hands in a plank position. Your elbows should be extended and your body straight. Do not allow your hips to sag. This will be your starting position.', 'To begin, allow the elbows to flex, lowering your chest to the floor as you inhale.', 'Using your pectoral muscles, press your upper body back up to the starting position by extending the elbows. Exhale as you perform this step.', 'After pausing at the contracted position, repeat the movement for the prescribed amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Push-Ups - Close Grip', 'Triceps', 'Bodyweight', 'Triceps', array['Chest', 'Shoulders'], array['Lie on the floor face down and place your hands closer than shoulder width for a close hand position. Make sure that you are holding your torso up at arms'' length.', 'Lower yourself until your chest almost touches the floor as you inhale.', 'Using your triceps and some of your pectoral muscles, press your upper body back up to the starting position and squeeze your chest. Breathe out as you perform this step.', 'After a second pause at the contracted position, repeat the movement for the prescribed amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Push Press', 'Shoulders', 'Barbell', 'Shoulders', array['Quadriceps', 'Triceps'], array[]::text[]);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Push Press - Behind the Neck', 'Shoulders', 'Barbell', 'Shoulders', array['Calves', 'Quadriceps', 'Triceps'], array['Standing with the weight racked on the back of the shoulders, begin with the dip. With your feet directly under your hips, flex the knees without moving the hips backward. Go down only slightly, and reverse direction as powerfully as possible. Drive through the heels create as much speed and force as possible, moving the bar in a vertical path.', 'Using the momentum generated, finish pressing the weight overhead be extending through the arms.', 'Return to the starting position, using your legs to absorb the impact.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Pushups', 'Chest', 'Bodyweight', 'Chest', array['Shoulders', 'Triceps'], array['Lie on the floor face down and place your hands about 36 inches apart while holding your torso up at arms length.', 'Next, lower yourself downward until your chest almost touches the floor as you inhale.', 'Now breathe out and press your upper body back up to the starting position while squeezing your chest.', 'After a brief pause at the top contracted position, you can begin to lower yourself downward again for as many repetitions as needed.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Rack Pulls', 'Lower back', 'Barbell', 'Lower back', array['Forearms', 'Glutes', 'Hamstrings', 'Traps'], array['Set up in a power rack with the bar on the pins. The pins should be set to the desired point; just below the knees, just above, or in the mid thigh position. Position yourself against the bar in proper deadlifting position. Your feet should be under your hips, your grip shoulder width, back arched, and hips back to engage the hamstrings. Since the weight is typically heavy, you may use a mixed grip, a hook grip, or use straps to aid in holding the weight.', 'With your head looking forward, extend through the hips and knees, pulling the weight up and back until lockout. Be sure to pull your shoulders back as you complete the movement.', 'Return the weight to the pins and repeat.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Reverse Barbell Curl', 'Biceps', 'Barbell', 'Biceps', array['Forearms'], array['Stand up with your torso upright while holding a barbell at shoulder width with the elbows close to the torso. The palm of your hands should be facing down (pronated grip). This will be your starting position.', 'While holding the upper arms stationary, curl the weights while contracting the biceps as you breathe out. Only the forearms should move. Continue the movement until your biceps are fully contracted and the bar is at shoulder level. Hold the contracted position for a second as you squeeze the muscle.', 'Slowly begin to bring the bar back to starting position as your breathe in.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Reverse Barbell Preacher Curls', 'Biceps', 'Barbell', 'Biceps', array['Forearms'], array['Grab an EZ-bar using a shoulder width and palms down (pronated) grip.', 'Now place the upper part of both arms on top of the preacher bench and have your arms extended. This will be your starting position.', 'As you exhale, use the biceps to curl the weight up until your biceps are fully contracted and the barbell is at shoulder height. Squeeze the biceps hard for a second at the contracted position.', 'As you breathe in, slowly lower the barbell until your upper arms are extended and the biceps is fully stretched.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Reverse Cable Curl', 'Biceps', 'Cable', 'Biceps', array['Forearms'], array['Stand up with your torso upright while holding a bar attachment that is attached to a low pulley using a pronated (palms down) and shoulder width grip. Make sure also that you keep the elbows close to the torso. This will be your starting position.', 'While holding the upper arms stationary, curl the weights while contracting the biceps as you breathe out. Only the forearms should move. Continue the movement until your biceps are fully contracted and the bar is at shoulder level. Hold the contracted position for a second as you squeeze the muscle.', 'Slowly begin to bring the bar back to starting position as your breathe in.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Reverse Crunch', 'Abdominals', 'Bodyweight', 'Abdominals', array[]::text[], array['Lie down on the floor with your legs fully extended and arms to the side of your torso with the palms on the floor. Your arms should be stationary for the entire exercise.', 'Move your legs up so that your thighs are perpendicular to the floor and feet are together and parallel to the floor. This is the starting position.', 'While inhaling, move your legs towards the torso as you roll your pelvis backwards and you raise your hips off the floor. At the end of this movement your knees will be touching your chest.', 'Hold the contraction for a second and move your legs back to the starting position while exhaling.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Reverse Flyes', 'Shoulders', 'Dumbbell', 'Shoulders', array[]::text[], array['To begin, lie down on an incline bench with the chest and stomach pressing against the incline. Have the dumbbells in each hand with the palms facing each other (neutral grip).', 'Extend the arms in front of you so that they are perpendicular to the angle of the bench. The legs should be stationary while applying pressure with the ball of your toes. This is the starting position.', 'Maintaining the slight bend of the elbows, move the weights out and away from each other (to the side) in an arc motion while exhaling. Tip: Try to squeeze your shoulder blades together to get the best results from this exercise.', 'The arms should be elevated until they are parallel to the floor.', 'Feel the contraction and slowly lower the weights back down to the starting position while inhaling.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Reverse Grip Bent-Over Rows', 'Middle back', 'Barbell', 'Middle back', array['Biceps', 'Lats', 'Shoulders'], array['Stand erect while holding a barbell with a supinated grip (palms facing up).', 'Bend your knees slightly and bring your torso forward, by bending at the waist, while keeping the back straight until it is almost parallel to the floor. Tip: Make sure that you keep the head up. The barbell should hang directly in front of you as your arms hang perpendicular to the floor and your torso. This is your starting position.', 'While keeping the torso stationary, lift the barbell as you breathe out, keeping the elbows close to the body and not doing any force with the forearm other than holding the weights. On the top contracted position, squeeze the back muscles and hold for a second.', 'Slowly lower the weight again to the starting position as you inhale.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Reverse Machine Flyes', 'Shoulders', 'Machine', 'Shoulders', array[]::text[], array['Adjust the handles so that they are fully to the rear. Make an appropriate weight selection and adjust the seat height so the handles are at shoulder level. Grasp the handles with your hands facing inwards. This will be your starting position.', 'In a semicircular motion, pull your hands out to your side and back, contracting your rear delts.', 'Keep your arms slightly bent throughout the movement, with all of the motion occurring at the shoulder joint.', 'Pause at the rear of the movement, and slowly return the weight to the starting position.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Romanian Deadlift', 'Hamstrings', 'Barbell', 'Hamstrings', array['Calves', 'Glutes', 'Lower back'], array['Put a barbell in front of you on the ground and grab it using a pronated (palms facing down) grip that a little wider than shoulder width. Tip: Depending on the weight used, you may need wrist wraps to perform the exercise and also a raised platform in order to allow for better range of motion.', 'Bend the knees slightly and keep the shins vertical, hips back and back straight. This will be your starting position.', 'Keeping your back and arms completely straight at all times, use your hips to lift the bar as you exhale. Tip: The movement should not be fast but steady and under control.', 'Once you are standing completely straight up, lower the bar by pushing the hips back, only slightly bending the knees, unlike when squatting. Tip: Take a deep breath at the start of the movement and keep your chest up. Hold your breath as you lower and exhale as you complete the movement.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Rope Straight-Arm Pulldown', 'Lats', 'Cable', 'Lats', array[]::text[], array['Attach a rope to a high pulley and make your weight selection. Stand a couple feet back from the pulley with your feet staggered and take the rope with both hands. Lean forward from the hip, keeping your back straight, with your arms extended up in front of you. This will be your starting position.', 'Keeping your arms straight, extend the shoulder to pull the rope down to your thighs.', 'Pause at the bottom of the motion, squeezing your lats.', 'Return to the starting position without allowing the weight to fully rest on the stack.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Russian Twist', 'Abdominals', 'Bodyweight', 'Abdominals', array['Lower back'], array['Lie down on the floor placing your feet either under something that will not move or by having a partner hold them. Your legs should be bent at the knees.', 'Elevate your upper body so that it creates an imaginary V-shape with your thighs. Your arms should be fully extended in front of you perpendicular to your torso and with the hands clasped. This is the starting position.', 'Twist your torso to the right side until your arms are parallel with the floor while breathing out.', 'Hold the contraction for a second and move back to the starting position while breathing out. Now move to the opposite side performing the same techniques you applied to the right side.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Scapular Pull-Up', 'Traps', null, 'Traps', array['Lats', 'Middle back'], array['Take a pronated grip on a pull-up bar.', 'From a hanging position, raise yourself a few inches without using your arms. Do this by depressing your shoulder girdle in a reverse shrugging motion.', 'Pause at the completion of the movement, and then slowly return to the starting position before performing more repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Seated Barbell Military Press', 'Shoulders', 'Barbell', 'Shoulders', array['Triceps'], array['Sit on a Military Press Bench with a bar behind your head and either have a spotter give you the bar (better on the rotator cuff this way) or pick it up yourself carefully with a pronated grip (palms facing forward). Tip: Your grip should be wider than shoulder width and it should create a 90-degree angle between the forearm and the upper arm as the barbell goes down.', 'Once you pick up the barbell with the correct grip length, lift the bar up over your head by locking your arms. Hold at about shoulder level and slightly in front of your head. This is your starting position.', 'Lower the bar down to the collarbone slowly as you inhale.', 'Lift the bar back up to the starting position as you exhale.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Seated Bent-Over Rear Delt Raise', 'Shoulders', 'Dumbbell', 'Shoulders', array[]::text[], array['Place a couple of dumbbells looking forward in front of a flat bench.', 'Sit on the end of the bench with your legs together and the dumbbells behind your calves.', 'Bend at the waist while keeping the back straight in order to pick up the dumbbells. The palms of your hands should be facing each other as you pick them. This will be your starting position.', 'Keeping your torso forward and stationary, and the arms slightly bent at the elbows, lift the dumbbells straight to the side until both arms are parallel to the floor. Exhale as you lift the weights. (Note: avoid swinging the torso or bringing the arms back as opposed to the side.)', 'After a one second contraction at the top, slowly lower the dumbbells back to the starting position.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Seated Cable Rows', 'Middle back', 'Cable', 'Middle back', array['Biceps', 'Lats', 'Shoulders'], array['For this exercise you will need access to a low pulley row machine with a V-bar. Note: The V-bar will enable you to have a neutral grip where the palms of your hands face each other. To get into the starting position, first sit down on the machine and place your feet on the front platform or crossbar provided making sure that your knees are slightly bent and not locked.', 'Lean over as you keep the natural alignment of your back and grab the V-bar handles.', 'With your arms extended pull back until your torso is at a 90-degree angle from your legs. Your back should be slightly arched and your chest should be sticking out. You should be feeling a nice stretch on your lats as you hold the bar in front of you. This is the starting position of the exercise.', 'Keeping the torso stationary, pull the handles back towards your torso while keeping the arms close to it until you touch the abdominals. Breathe out as you perform that movement. At that point you should be squeezing your back muscles hard. Hold that contraction for a second and slowly go back to the original position while breathing in.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Seated Cable Shoulder Press', 'Shoulders', 'Cable', 'Shoulders', array['Triceps'], array['Adjust the weight to an appropriate amount and be seated, grasping the handles. Your upper arms should be about 90 degrees to the body, with your head and chest up. The elbows should also be bent to about 90 degrees. This will be your starting position.', 'Begin by extending through the elbow, pressing the handles together above your head.', 'After pausing at the top, return the handles to the starting position. Ensure that you maintain tension on the cables.', 'You can also execute this movement with your back off the pad and alternate hands.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Seated Calf Raise', 'Calves', 'Machine', 'Calves', array[]::text[], array['Sit on the machine and place your toes on the lower portion of the platform provided with the heels extending off. Choose the toe positioning of your choice (forward, in, or out) as per the beginning of this chapter.', 'Place your lower thighs under the lever pad, which will need to be adjusted according to the height of your thighs. Now place your hands on top of the lever pad in order to prevent it from slipping forward.', 'Lift the lever slightly by pushing your heels up and release the safety bar. This will be your starting position.', 'Slowly lower your heels by bending at the ankles until the calves are fully stretched. Inhale as you perform this movement.', 'Raise the heels by extending the ankles as high as possible as you contract the calves and breathe out. Hold the top contraction for a second.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Seated Close-Grip Concentration Barbell Curl', 'Biceps', 'Barbell', 'Biceps', array[]::text[], array['Sit down on a flat bench with a barbell or E-Z Bar in front of you in between your legs. Your legs should be spread with the knees bent and the feet on the floor.', 'Use your arms to pick the barbell up and place the back of your upper arms on top of your inner thighs (around three and a half inches away from the front of the knee). A supinated grip closer than shoulder width is needed to perform this exercise. Tip: Your arm should be extended at arms length and the barbell should be above the floor. This will be your starting position.', 'While holding the upper arms stationary, curl the weights forward while contracting the biceps as you breathe out. Only the forearms should move. Continue the movement until your biceps are fully contracted and the dumbbells are at shoulder level. Hold the contracted position for a second as you squeeze the biceps.', 'Slowly begin to bring the barbell back to starting position as your breathe in. Tip: Avoid swinging motions at any time.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Seated Dumbbell Curl', 'Biceps', 'Dumbbell', 'Biceps', array[]::text[], array['Sit on a flat bench with a dumbbell on each hand being held at arms length. The elbows should be close to the torso.', 'Rotate the palms of the hands so that they are facing your torso. This will be your starting position.', 'While holding the upper arm stationary, curl the weights and start twisting the wrists once the dumbbells pass your thighs so that the palms of your hands face forward at the end of the movement. Make sure that you contract the biceps as you breathe out and make sure that only the forearms move. Continue the movement until your biceps are fully contracted and the dumbbells are at shoulder level. Hold the contracted position for a second as you squeeze the biceps.', 'Slowly begin to bring the dumbbells back to the starting position as your breathe in and as you rotate the wrists back to a neutral grip.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Seated Dumbbell Palms-Down Wrist Curl', 'Forearms', 'Dumbbell', 'Forearms', array[]::text[], array['Start out by placing two dumbbells on the floor in front of a flat bench.', 'Sit down on the edge of the flat bench with your legs at about shoulder width apart. Make sure to keep your feet on the floor.', 'Use your arms to grab both of the dumbbells and bring them up so that your forearms are resting against your thighs with the palms of the hands facing down. Your wrists should be hanging over the edge of your thighs.', 'Start out by curling your wrist upwards and exhaling.', 'Slowly lower your wrists back down to the starting position while inhaling. Make sure to inhale during this part of the exercise. Tip: Your forearms should be stationary as your wrist is the only movement needed to perform this exercise.', 'Repeat for the recommended amount of repetitions.', 'When finished, simply lower the dumbbells to the floor.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Seated Dumbbell Palms-Up Wrist Curl', 'Forearms', 'Dumbbell', 'Forearms', array[]::text[], array['Start out by placing two dumbbells on the floor in front of a flat bench.', 'Sit down on the edge of the flat bench with your legs at about shoulder width apart. Make sure to keep your feet on the floor.', 'Use your arms to grab both of the dumbbells and bring them up so that your forearms are resting against your thighs with the palms of the hands facing up. Your wrists should be hanging over the edge of your thighs.', 'Start out by curling your wrist upwards and exhaling.', 'Slowly lower your wrists back down to the starting position while inhaling. Make sure to inhale during this part of the exercise. Tip: Your forearms should be stationary as your wrist is the only movement needed to perform this exercise.', 'Repeat for the recommended amount of repetitions.', 'When finished, simply lower the dumbbells to the floor.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Seated Dumbbell Press', 'Shoulders', 'Dumbbell', 'Shoulders', array['Triceps'], array['Grab a couple of dumbbells and sit on a military press bench or a utility bench that has a back support on it as you place the dumbbells upright on top of your thighs.', 'Clean the dumbbells up one at a time by using your thighs to bring the dumbbells up to shoulder height at each side.', 'Rotate the wrists so that the palms of your hands are facing forward. This is your starting position.', 'As you exhale, push the dumbbells up until they touch at the top.', 'After a second pause, slowly come down back to the starting position as you inhale.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Seated Leg Curl', 'Hamstrings', 'Machine', 'Hamstrings', array[]::text[], array['Adjust the machine lever to fit your height and sit on the machine with your back against the back support pad.', 'Place the back of lower leg on top of padded lever (just a few inches under the calves) and secure the lap pad against your thighs, just above the knees. Then grasp the side handles on the machine as you point your toes straight (or you can also use any of the other two stances) and ensure that the legs are fully straight right in front of you. This will be your starting position.', 'As you exhale, pull the machine lever as far as possible to the back of your thighs by flexing at the knees. Keep your torso stationary at all times. Hold the contracted position for a second.', 'Slowly return to the starting position as you breathe in.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Seated One-arm Cable Pulley Rows', 'Middle back', 'Cable', 'Middle back', array['Biceps', 'Lats', 'Traps'], array['To get into the starting position, first sit down on the machine and place your feet on the front platform or crossbar provided making sure that your knees are slightly bent and not locked.', 'Lean over as you keep the natural alignment of your back and grab the single handle attachment with your left arm using a palms-down grip.', 'With your arm extended pull back until your torso is at a 90-degree angle from your legs. Your back should be slightly arched and your chest should be sticking out. You should be feeling a nice stretch on your lat as you hold the bar in front of you. The right arm can be kept by the waist. This is the starting position of the exercise.', 'Keeping the torso stationary, pull the handles back towards your torso while keeping the arms close to it as you rotate the wrist, so that by the time your hand is by your abdominals it is in a neutral position (palms facing the torso). Breathe out as you perform that movement. At that point you should be squeezing your back muscles hard.', 'Hold that contraction for a second and slowly go back to the original position while breathing in. Tip: Remember to rotate the wrist as you go back to the starting position so that the palms are facing down again.', 'Repeat for the recommended amount of repetitions and then perform the same movement with the right hand.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Seated Side Lateral Raise', 'Shoulders', 'Dumbbell', 'Shoulders', array[]::text[], array['Pick a couple of dumbbells and sit at the end of a flat bench with your feet firmly on the floor. Hold the dumbbells with your palms facing in and your arms straight down at your sides at arms'' length. This will be your starting position.', 'While maintaining the torso stationary (no swinging), lift the dumbbells to your side with a slight bend on the elbow and the hands slightly tilted forward as if pouring water in a glass. Continue to go up until you arms are parallel to the floor. Exhale as you execute this movement and pause for a second at the top.', 'Lower the dumbbells back down slowly to the starting position as you inhale.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Lateral Raise', 'Shoulders', 'Dumbbell', 'Shoulders', array[]::text[], array['Pick a couple of dumbbells and stand with a straight torso and the dumbbells by your side at arms length with the palms of the hand facing you. This will be your starting position.', 'While maintaining the torso in a stationary position (no swinging), lift the dumbbells to your side with a slight bend on the elbow and the hands slightly tilted forward as if pouring water in a glass. Continue to go up until you arms are parallel to the floor. Exhale as you execute this movement and pause for a second at the top.', 'Lower the dumbbells back down slowly to the starting position as you inhale.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Single-Arm Push-Up', 'Chest', 'Bodyweight', 'Chest', array['Shoulders', 'Triceps'], array['Begin laying prone on the ground. Move yourself into a position supporting your weight on your toes and one arm. Your working arm should be placed directly under the shoulder, fully extended. Your legs should be extended, and for this movement you may need a wider base, placing your feet further apart than in a normal push-up.', 'Maintain good posture, and place your free hand behind your back. This will be your starting position.', 'Lower yourself by allowing the elbow to flex until you touch the ground.', 'Descend slowly, and reverse direction be extending the arm to return to the starting position.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Single-Leg Leg Extension', 'Quadriceps', 'Machine', 'Quadriceps', array[]::text[], array['Seat yourself in the machine and adjust it so that you are positioned properly. The pad should be against the lower part of the shin but not in contact with the ankle. Adjust the seat so that the pivot point is in line with your knee. Select a weight appropriate for your abilities.', 'Maintaining good posture, fully extend one leg, pausing at the top of the motion.', 'Return to the starting position without letting the weight stop, keeping tension on the muscle.', 'Repeat for the desired number of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Single Leg Glute Bridge', 'Glutes', 'Bodyweight', 'Glutes', array['Hamstrings'], array['Lay on the floor with your feet flat and knees bent.', 'Raise one leg off of the ground, pulling the knee to your chest. This will be your starting position.', 'Execute the movement by driving through the heel, extending your hip upward and raising your glutes off of the ground.', 'Extend as far as possible, pause and then return to the starting position.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Sit-Up', 'Abdominals', 'Bodyweight', 'Abdominals', array[]::text[], array['Lie down on the floor placing your feet either under something that will not move or by having a partner hold them. Your legs should be bent at the knees.', 'Place your hands behind your head and lock them together by clasping your fingers. This is the starting position.', 'Elevate your upper body so that it creates an imaginary V-shape with your thighs. Breathe out when performing this part of the exercise.', 'Once you feel the contraction for a second, lower your upper body back down to the starting position while inhaling.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Smith Incline Shoulder Raise', 'Shoulders', 'Barbell', 'Shoulders', array['Chest'], array['Place an incline bench underneath the smith machine. Place the barbell at a height that you can reach when lying down and your arms are almost fully extended. Once the weight you need is selected, lie down on the incline bench and make sure your shoulders are aligned right under the barbell.', 'Using a shoulder width pronated (palms forward) grip, lift the bar from the rack and hold it straight over you with a slight bend at the elbows. This will be your starting position.', 'As you breathe out, lift the bar up until your arms are fully extended. Note: The contraction should be felt around the shoulders.', 'After a second pause, bring the bar back down to the starting position as you breathe in.', 'Repeat the movement for the prescribed amount of repetitions.', 'When you are done, place the bar back in the rack.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Smith Machine Bench Press', 'Chest', 'Machine', 'Chest', array['Shoulders', 'Triceps'], array['Place a flat bench underneath the smith machine. Now place the barbell at a height that you can reach when lying down and your arms are almost fully extended. Once the weight you need is selected, lie down on the flat bench. Using a pronated grip that is wider than shoulder width, unlock the bar from the rack and hold it straight over you with your arms locked. This will be your starting position.', 'As you breathe in, come down slowly until you feel the bar on your middle chest.', 'After a second pause, bring the bar back to the starting position as you breathe out and push the bar using your chest muscles. Lock your arms in the contracted position, hold for a second and then start coming down slowly again. Tip: It should take at least twice as long to go down than to come up.', 'Repeat the movement for the prescribed amount of repetitions.', 'When you are done, lock the bar back in the rack.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Smith Machine Bent Over Row', 'Middle back', 'Machine', 'Middle back', array['Biceps', 'Lats', 'Shoulders'], array['Set the barbell attached to the smith machine to a height that is about 2 inches below your knees.', 'Bend your knees slightly and bring your torso forward, by bending at the waist, while keeping the back straight until it is almost parallel to the floor. Tip: Make sure that you keep the head up.', 'Now grasp the barbell using an overhand (pronated) grip and unlock it from the smith machine rack. Then let it hang directly in front of you as your arms hang extended perpendicular to the floor and your torso. This is your starting position.', 'While keeping the torso stationary, lift the barbell as you breathe out, keeping the elbows close to the body and not doing any force with the forearm other than holding the weights. On the top contracted position, squeeze the back muscles and hold for a second.', 'Slowly lower the weight again to the starting position as you inhale.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Smith Machine Calf Raise', 'Calves', 'Machine', 'Calves', array[]::text[], array['Place a block or weight plate below the bar on the Smith machine. Set the bar to a position that best matches your height. Once the correct height is chosen and the bar is loaded, step onto the plates with the balls of your feet and place the bar on the back of your shoulders.', 'Take the bar with both hands facing forward. Rotate the bar to unrack it. This will be your starting position.', 'Raise your heels as high as possible by pushing off of the balls of your feet, flexing your calf at the top of the contraction. Your knees should remain extended. Hold the contracted position for a second before you start to go back down.', 'Return slowly to the starting position as you breathe in while lowering your heels.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Smith Machine Close-Grip Bench Press', 'Triceps', 'Machine', 'Triceps', array['Chest', 'Shoulders'], array['Place a flat bench underneath the smith machine. Place the barbell at a height that you can reach when lying down and your arms are almost fully extended. Once the weight you need is selected, lie down on the flat bench. Using a close and pronated grip (palms facing forward) that is around shoulder width, unlock the bar from the rack and hold it straight over you with your arms locked. This will be your starting position.', 'As you breathe in, come down slowly until you feel the bar on your middle chest. Tip: Make sure that as opposed to a regular bench press, you keep the elbows close to the torso at all times in order to maximize triceps involvement.', 'After a second pause, bring the bar back to the starting position as you breathe out and push the bar using your triceps muscles. Lock your arms in the contracted position, hold for a second and then start coming down slowly again. Tip: It should take at least twice as long to go down than to come up.', 'Repeat the movement for the prescribed amount of repetitions.', 'When you are done, lock the bar back in the rack.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Smith Machine Decline Press', 'Chest', 'Machine', 'Chest', array['Shoulders', 'Triceps'], array['Position a decline bench in the rack so that the bar will be above your chest. Load an appropriate weight and take your place on the bench.', 'Rotate the bar to unhook it from the rack and fully extend your arms. Your back should be slightly arched and your shoulder blades retracted. This will be your starting position.', 'Begin the movement by flexing your arms, lowering the bar to your chest.', 'Pause briefly, and then extend your arms to push the weight back to the starting position.', 'After completing the desired number of repetitions, rotate the bar to rack the weight.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Smith Machine Hip Raise', 'Abdominals', 'Machine', 'Abdominals', array[]::text[], array['Position a bench in the rack and load the bar to an appropriate weight. Lie down on the bench, placing the bottom of your feet against the bar. Unlock the bar and extend your legs. You may need to use your hands to assist you. For added stability grasp the sides of the Smith Machine. This will be your starting position.', 'Initiate the movement by rotating your pelvis, flexing your spine to raise your hips off of the bench. Maintain a slight bend in the knees throughout the motion.', 'After a brief pause, return the hips to the bench.', 'Repeat for the desired number of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Smith Machine Incline Bench Press', 'Chest', 'Machine', 'Chest', array['Shoulders', 'Triceps'], array['Place an incline bench underneath the smith machine. Place the barbell at a height that you can reach when lying down and your arms are almost fully extended. Once the weight you need is selected, lie down on the incline bench and make sure your upper chest is aligned with the barbell. Using a pronated grip (palms facing forward) that is wider than shoulder width, unlock the bar from the rack and hold it straight over you with your arms locked. This will be your starting position.', 'As you breathe in, come down slowly until you feel the bar on your upper chest.', 'After a second pause, bring the bar back to the starting position as you breathe out and push the bar using your chest muscles. Lock your arms in the contracted position, hold for a second and then start coming down slowly again. Tip: It should take at least twice as long to go down than to come up.', 'Repeat the movement for the prescribed amount of repetitions.', 'When you are done, place the bar back in the rack.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Smith Machine Overhead Shoulder Press', 'Shoulders', 'Machine', 'Shoulders', array['Triceps'], array['To begin, place a flat bench (or preferably one with back support) underneath a smith machine. Position the barbell at a height so that when seated on the flat bench, the arms must be almost fully extended to reach the barbell.', 'Once you have the correct height, sit slightly in behind the barbell so that there is an imaginary straight line from the tip of your nose to the barbell. Your feet should be stationary. Grab the barbell with the palms facing forward, unlock it and lift it up so that your arms are fully extended. This is the starting position.', 'Slowly begin to lower the barbell until it is level with your chin while inhaling.', 'Then lift the barbell back to the starting position using your shoulders while exhaling.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Smith Machine Pistol Squat', 'Quadriceps', 'Machine', 'Quadriceps', array['Calves', 'Glutes', 'Hamstrings'], array['To begin, first set the bar to a position that best matches your height. Step under it and position the bar across the back of your shoulders.', 'Take the bar with your hands facing forward, unlock it and lift it off the rack by extending your legs. 3', 'Move one foot forward about 12 inches in front of the bar. Extend the other leg out in front of you, holding it off the ground. Look forward at all times and maintain a neutral or slightly arched spine. This will be your starting position.', 'Maintaining good posture, lower yourself by flexing the knee and hip, going down as far as flexibility allows.', 'Pause briefly at the bottom and then return to the starting position by driving through the heel of your foot, extending the knee and hip.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Smith Machine Reverse Calf Raises', 'Calves', 'Machine', 'Calves', array[]::text[], array['Adjust the barbell on the smith machine to fit your height and align a raised platform right under the bar.', 'Stand on the platform with the heels of your feet secured on top of it with the balls of your feet extending off it. Position your toes facing forward with a shoulder width stance.', 'Now, place your shoulders under the barbell while maintaining the foot positioning described and push the barbell up by extending your hips and knees until your torso is standing erect. The knees should be kept with a slight bend; never locked. This will be your starting position. Tip: The barbell on your back is only for balance purposes.', 'Raise the balls of your feet as you breathe out by extending your toes as high as possible and flexing your calf. Ensure that the knee is kept stationary at all times. There should be no bending at any time. Hold the contracted position for a second before you start to go back down.', 'Slowly go back down to the starting position as you breathe in by lowering the balls of your feet and toes.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Smith Machine Squat', 'Quadriceps', 'Machine', 'Quadriceps', array['Calves', 'Glutes', 'Hamstrings', 'Lower back'], array['To begin, first set the bar on the height that best matches your height. Once the correct height is chosen and the bar is loaded, step under the bar and place the back of your shoulders (slightly below the neck) across it.', 'Hold on to the bar using both arms at each side (palms facing forward), unlock it and lift it off the rack by first pushing with your legs and at the same time straightening your torso.', 'Position your legs using a shoulder width medium stance with the toes slightly pointed out. Keep your head up at all times and also maintain a straight back. This will be your starting position. (Note: For the purposes of this discussion we will use the medium stance which targets overall development; however you can choose any of the three stances discussed in the foot stances section).', 'Begin to slowly lower the bar by bending the knees as you maintain a straight posture with the head up. Continue down until the angle between the upper leg and the calves becomes slightly less than 90-degrees (which is the point in which the upper legs are below parallel to the floor). Inhale as you perform this portion of the movement. Tip: If you performed the exercise correctly, the front of the knees should make an imaginary straight line with the toes that is perpendicular to the front. If your knees are past that imaginary line (if they are past your toes) then you are placing undue stress on the knee and the exercise has been performed incorrectly.', 'Begin to raise the bar as you exhale by pushing the floor with the heel of your foot as you straighten the legs again and go back to the starting position.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Smith Machine Stiff-Legged Deadlift', 'Hamstrings', 'Machine', 'Hamstrings', array['Glutes', 'Lower back'], array['To begin, set the bar on the smith machine to a height that is around the middle of your thighs. Once the correct height is chosen and the bar is loaded, grasp the bar using a pronated (palms forward) grip that is shoulder width apart. You may need some wrist wraps if using a significant amount of weight.', 'Lift the bar up by fully extending your arms while keeping your back straight. Stand with your torso straight and your legs spaced using a shoulder width or narrower stance. The knees should be slightly bent. This is your starting position.', 'Keeping the knees stationary, lower the barbell to over the top of your feet by bending at the waist while keeping your back straight. Keep moving forward as if you were going to pick something from the floor until you feel a stretch on the hamstrings. Exhale as you perform this movement', 'Start bringing your torso up straight again as soon as you feel the hamstrings stretch by extending your hips and waist until you are back at the starting position. Inhale as you perform this movement.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Smith Machine Upright Row', 'Traps', 'Machine', 'Traps', array['Biceps', 'Middle back', 'Shoulders'], array['To begin, set the bar on the smith machine to a height that is around the middle of your thighs. Once the correct height is chosen and the bar is loaded, grasp the bar using a pronated (palms forward) grip that is shoulder width apart. You may need some wrist wraps if using a significant amount of weight.', 'Lift the barbell up and fully extend your arms with your back straight. There should be a slight bend at the elbows. This is the starting position.', 'Use your side shoulders to lift the bar as you exhale. The bar should be close to the body as you move it up. Continue to lift it until it nearly touches your chin. Tip: Your elbows should drive the motion. As you lift the bar, your elbows should always be higher than your forearms. Also, keep your torso stationary and pause for a second at the top of the movement.', 'Lower the bar back down slowly to the starting position. Inhale as you perform this portion of the movement.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Smith Single-Leg Split Squat', 'Quadriceps', 'Machine', 'Quadriceps', array['Calves', 'Glutes', 'Hamstrings'], array['To begin, place a flat bench 2-3 feet behind the smith machine. Then, set the bar on the height that best matches your height. Once the correct height is chosen and the bar is loaded, step under the bar and place the back of your shoulders (slightly below the neck) across it.', 'Hold on to the bar using both arms at each side (palms facing forward), unlock it and lift it off the rack by first pushing with your legs and at the same time straightening your torso.', 'Position your legs by placing one foot slightly forward under the bar and extending your other leg back and place the top of your foot on the bench. This will be your starting position', 'Begin to slowly lower the bar by bending the knee as you maintain a straight posture with the head up. Continue down until the angle between the upper leg and the calf becomes slightly less than 90-degrees (which is the point in which the upper legs are below parallel to the floor). Inhale as you perform this portion of the movement. Tip: If you performed the exercise correctly, the front of the knee should make an imaginary straight line with the toes that is perpendicular to the front. If your knee is past that imaginary line (if it is past your toes) then you are placing undue stress on the knee and the exercise has been performed incorrectly.', 'Begin to raise the bar as you exhale by pushing the floor with the heel of your foot mainly as you straighten your leg again and go back to the starting position.', 'Repeat for the recommended amount of repetitions.', 'Switch legs and repeat the movement.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Snatch', 'Quadriceps', 'Barbell', 'Quadriceps', array['Biceps', 'Glutes', 'Hamstrings', 'Lower back', 'Shoulders', 'Traps', 'Triceps'], array['Place your feet at a shoulder width stance with the barbell resting right above the connection between the toes and the rest of the foot.', 'With a palms facing down grip, bend at the knees and keeping the back flat grab the bar using a wider than shoulder width grip. Bring the hips down and make sure that your body drops as if you were going to sit on a chair. This will be your starting position.', 'Start pushing the floor as if it were a moving platform with your feet and simultaneously start lifting the barbell keeping it close to your legs.', 'As the bar reaches the middle of your thighs, push the floor with your legs and lift your body to a complete extension in an explosive motion.', 'Lift your shoulders back in a shrugging movement as you bring the bar up while lifting your elbows out to the side and keeping them above the bar for as long as possible.', 'Now in a very quick but powerful motion, you have to get your body under the barbell when it has reached a high enough point where it can be controlled and drop while locking your arms and holding the barbell overhead as you assume a squat position.', 'Finalize the movement by rising up out of the squat position to finish the lift. At the end of the lift both feet should be on line and the arms fully extended holding the barbell overhead.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Snatch Balance', 'Quadriceps', 'Barbell', 'Quadriceps', array['Calves', 'Glutes', 'Hamstrings', 'Shoulders', 'Triceps'], array['Begin with the feet in the pulling position, the bar racked across the back of the shoulders, and the hands placed in a wide snatch grip.', 'Pop the bar with an abrupt dip and drive of the knees, and aggressively drive under the bar, transitioning the feet into the receiving position.', 'Receive the bar locked out overhead near the bottom of the squat. The torso should remain vertical, lowering the hips between the legs.', 'Continue to descend to full depth, and return to a standing position. Carefully lower the weight.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Snatch Deadlift', 'Hamstrings', 'Barbell', 'Hamstrings', array['Forearms', 'Glutes', 'Hamstrings', 'Lower back', 'Quadriceps', 'Traps'], array['The snatch deadlift strengthens the first pull of the snatch. Begin with a wide snatch grip with the barbell placed on the platform. The feet should be directly under the hips, with the feet turned out. Squat down to the bar, keeping the back in absolute extension with the head facing forward.', 'Initiate the movement by driving through the heels, raising the hips. The back angle should remain the same until the bar passes the knees.', 'At that point, drive your hips through the bar as you lay back. Return the bar to the platform by reversing the motion.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Snatch Pull', 'Hamstrings', 'Barbell', 'Hamstrings', array['Calves', 'Glutes', 'Lower back', 'Quadriceps', 'Traps'], array['With a barbell on the floor close to the shins, take a wide snatch grip. Lower your hips with the weight focused on the heels, back straight, head facing forward, chest up, with your shoulders just in front of the bar. This will be your starting position.', 'Begin the first pull by driving through the heels, extending your knees. Your back angle should stay the same, and your arms should remain straight. Move the weight with control as you continue to above the knees.', 'Next comes the second pull, the main source of acceleration for the pull. As the bar approaches the mid-thigh position, begin extending through the hips. In a jumping motion, accelerate by extending the hips, knees, and ankles, using speed to move the bar upward.', 'There should be no need to actively pull through the arms to accelerate the weight; at the end of the second pull, the body should be fully extended, leaning slightly back. Full extension should be violent and abrupt, and ensure that you do not prolong the extension for longer than necessary.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Snatch Shrug', 'Traps', 'Barbell', 'Traps', array['Forearms', 'Shoulders'], array['Begin with a wide grip, with the bar hanging at the mid thigh position. You can use a hook or overhand grip. Your back should be straight and inclined slightly forward.', 'Shrug your shoulders towards your ears. While this exercise can usually by loaded with heavier weight than a snatch, avoid overloading to the point that the execution slows down.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Snatch from Blocks', 'Quadriceps', 'Barbell', 'Quadriceps', array['Calves', 'Forearms', 'Glutes', 'Hamstrings', 'Lower back', 'Shoulders', 'Traps', 'Triceps'], array['Begin with a loaded barbell on boxes or stands of the desired height. A wide grip should be taken on the bar. The feet should be directly below the hips, with the feet turned out as needed. Lower the hips, with the chest up and the head looking forward. The shoulders should be just in front of the bar, with the elbows pointed out. This will be the starting position.', 'Begin the first pull by driving through the front of the heels, raising the bar from the boxes.', 'Transition into the second pull by extending through the hips knees and ankles, driving the bar up as quickly as possible. The bar should be close to the body. At peak extension, shrug the shoulders and allow the elbows to flex to the side.', 'As you move your feet into the receiving position, forcefully pull yourself below the bar as you elevate the bar overhead. The feet should move to just outside the hips, turned out as necessary. Receive the bar with your body as low as possible and the arms fully extended overhead.', 'Keeping the bar aligned over the front of the heels, your head and chest up, drive through heels of the feet to move to a standing position. Carefully return the weight to the boxes.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Split Clean', 'Quadriceps', 'Barbell', 'Quadriceps', array['Calves', 'Forearms', 'Glutes', 'Hamstrings', 'Lower back', 'Shoulders', 'Traps'], array['With a barbell on the floor close to the shins, take an overhand grip just outside the legs. Lower your hips with the weight focused on the heels, back straight, head facing forward, chest up, with your shoulders just in front of the bar. This will be your starting position.', 'Begin the first pull by driving through the heels, extending your knees. Your back angle should stay the same, and your arms should remain straight. Move the weight with control as you continue to above the knees.', 'Next comes the second pull, the main source of acceleration for the clean. As the bar approaches the mid-thigh position, begin extending through the hips. In a jumping motion, accelerate by extending the hips, knees, and ankles, using speed to move the bar upward. There should be no need to actively pull through the arms to accelerate the weight; at the end of the second pull, the body should be fully extended, leaning slightly back, with the arms still extended.', 'As full extension is achieved, transition into the third pull by aggressively shrugging and flexing the arms with the elbows up and out. At peak extension, aggressively pull yourself down, rotating your elbows under the bar as you do so.', 'Receive the bar with the feet split, aggressively moving one foot forward and one foot back. The bar should be racked onto the protracted shoulders, lightly touching the throat with the hands relaxed. Continue to descend to the bottom position, which will help in the recovery.', 'Immediately recover by driving through the heels, keeping the torso upright and elbows up. Bring the feet together as you stand up.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Split Jerk', 'Quadriceps', 'Barbell', 'Quadriceps', array['Glutes', 'Hamstrings', 'Shoulders', 'Triceps'], array['Standing with the weight racked on the front of the shoulders, begin with the dip. With your feet directly under your hips, flex the knees without moving the hips backward.', 'Go down only slightly, and reverse direction as powerfully as possible. Drive through the heels create as much speed and force as possible, and be sure to move your head out of the way as the bar leaves the shoulders. At this moment as the feet leave the floor, the feet must be placed into the receiving position as quickly as possible.', 'In the brief moment the feet are not actively driving against the platform, the athlete''s effort to push the bar up will drive them down. The feet should be moved to a split stance, one foot forward, one foot back, with the knees partially bent. Receive the bar with the arms locked out overhead.', 'Return to a standing position, bringing the feet together.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Split Snatch', 'Hamstrings', 'Barbell', 'Hamstrings', array['Calves', 'Forearms', 'Glutes', 'Hamstrings', 'Lower back', 'Quadriceps', 'Shoulders', 'Traps', 'Triceps'], array['Begin with a loaded barbell on the floor. The bar should be close to or touching the shins, and a wide grip should be taken on the bar. The feet should be directly below the hips, with the feet turned out as needed. Lower the hips, with the chest up and the head looking forward. The shoulders should be just in front of the bar. This will be the starting position.', 'Begin the first pull by driving through the front of the heels, raising the bar from the ground. The back angle should stay the same until the bar passes the knees.', 'Transition into the second pull by extending through the hips knees and ankles, driving the bar up as quickly as possible. The bar should be close to the body. At peak extension, shrug the shoulders and allow the elbows to flex to the side.', 'As you move your feet into the receiving position, forcefully pull yourself below the bar as you elevate the bar overhead. The feet should move forcefully to a split position, one foot forward one foot back. Receive the bar with your body as low as possible and the arms fully extended overhead.', 'Keeping the bar aligned over the front of the heels, your head and chest up, drive through heels of the feet to move to a standing position, bringing your feet together.', 'Carefully return the weight to floor.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Split Squat with Dumbbells', 'Quadriceps', 'Dumbbell', 'Quadriceps', array['Glutes', 'Hamstrings'], array['Position yourself into a staggered stance with the rear foot elevated and front foot forward.', 'Hold a dumbbell in each hand, letting them hang at the sides. This will be your starting position.', 'Begin by descending, flexing your knee and hip to lower your body down. Maintain good posture througout the movement. Keep the front knee in line with the foot as you perform the exercise.', 'At the bottom of the movement, drive through the heel to extend the knee and hip to return to the starting position.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Squat Jerk', 'Quadriceps', 'Barbell', 'Quadriceps', array['Calves', 'Glutes', 'Hamstrings', 'Shoulders', 'Triceps'], array['Standing with the weight racked on the front of the shoulders, begin with the dip. With your feet directly under your hips, flex the knees without moving the hips backward. Go down only slightly, and reverse direction as powerfully as possible. Drive through the heels create as much speed and force as possible, and be sure to move your head out of the way as the bar leaves the shoulders.', 'At this moment as the feet leave the floor, the feet must be placed into the receiving position as quickly as possible. In the brief moment the feet are not actively driving against the platform, the athlete''s effort to push the bar up will drive them down. The feet should move forcefully to just outside the hips, turned out as necessary. Receive the bar with your body in a full squat and the arms fully extended overhead.', 'Keeping the bar aligned over the front of the heels, your head and chest up, drive throught heels of the feet to move to a standing position. Carefully return the weight to floor.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Standing Barbell Calf Raise', 'Calves', 'Barbell', 'Calves', array[]::text[], array['This exercise is best performed inside a squat rack for safety purposes. To begin, first set the bar on a rack that best matches your height. Once the correct height is chosen and the bar is loaded, step under the bar and place the bar on the back of your shoulders (slightly below the neck).', 'Hold on to the bar using both arms at each side and lift it off the rack by first pushing with your legs and at the same time straightening your torso.', 'Step away from the rack and position your legs using a shoulder width medium stance with the toes slightly pointed out. Keep your head up at all times as looking down will get you off balance and also maintain a straight back. The knees should be kept with a slight bend; never locked. This will be your starting position. Tip: For better range of motion you may also place the ball of your feet on a wooden block but be careful as this option requires more balance and a sturdy block.', 'Raise your heels as you breathe out by extending your ankles as high as possible and flexing your calf. Ensure that the knee is kept stationary at all times. There should be no bending at any time. Hold the contracted position by a second before you start to go back down.', 'Go back slowly to the starting position as you breathe in by lowering your heels as you bend the ankles until calves are stretched.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Standing Biceps Cable Curl', 'Biceps', 'Cable', 'Biceps', array[]::text[], array['Stand up with your torso upright while holding a cable curl bar that is attached to a low pulley. Grab the cable bar at shoulder width and keep the elbows close to the torso. The palm of your hands should be facing up (supinated grip). This will be your starting position.', 'While holding the upper arms stationary, curl the weights while contracting the biceps as you breathe out. Only the forearms should move. Continue the movement until your biceps are fully contracted and the bar is at shoulder level. Hold the contracted position for a second as you squeeze the muscle.', 'Slowly begin to bring the curl bar back to starting position as your breathe in.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Standing Cable Chest Press', 'Chest', 'Cable', 'Chest', array['Shoulders', 'Triceps'], array['Position dual pulleys to chest height and select an appropriate weight. Stand a foot or two in front of the cables, holding one in each hand. You can stagger your stance for better stability.', 'Position the upper arm at a 90 degree angle with the shoulder blades together. This will be your starting position.', 'Keeping the rest of the body stationary, extend through the elbows to press the handles forward, drawing them together in front of you.', 'Pause at the top of the motion, and return to the starting position.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Standing Calf Raises', 'Calves', 'Machine', 'Calves', array[]::text[], array['Adjust the padded lever of the calf raise machine to fit your height.', 'Place your shoulders under the pads provided and position your toes facing forward (or using any of the two other positions described at the beginning of the chapter). The balls of your feet should be secured on top of the calf block with the heels extending off it. Push the lever up by extending your hips and knees until your torso is standing erect. The knees should be kept with a slight bend; never locked. Toes should be facing forward, outwards or inwards as described at the beginning of the chapter. This will be your starting position.', 'Raise your heels as you breathe out by extending your ankles as high as possible and flexing your calf. Ensure that the knee is kept stationary at all times. There should be no bending at any time. Hold the contracted position by a second before you start to go back down.', 'Go back slowly to the starting position as you breathe in by lowering your heels as you bend the ankles until calves are stretched.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Standing Dumbbell Press', 'Shoulders', 'Dumbbell', 'Shoulders', array['Triceps'], array['Standing with your feet shoulder width apart, take a dumbbell in each hand. Raise the dumbbells to head height, the elbows out and about 90 degrees. This will be your starting position.', 'Maintaining strict technique with no leg drive or leaning back, extend through the elbow to raise the weights together directly above your head.', 'Pause, and slowly return the weight to the starting position.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Standing Dumbbell Reverse Curl', 'Biceps', 'Dumbbell', 'Biceps', array['Forearms'], array['To begin, stand straight with a dumbbell in each hand using a pronated grip (palms facing down). Your arms should be fully extended while your feet are shoulder width apart from each other. This is the starting position.', 'While holding the upper arms stationary, curl the weights while contracting the biceps as you breathe out. Only the forearms should move. Continue the movement until your biceps are fully contracted and the dumbbells are at shoulder level. Hold the contracted position for a second as you squeeze the muscle.', 'Slowly begin to bring the dumbbells back to starting position as your breathe in.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Standing Dumbbell Triceps Extension', 'Triceps', 'Dumbbell', 'Triceps', array[]::text[], array['To begin, stand up with a dumbbell held by both hands. Your feet should be about shoulder width apart from each other. Slowly use both hands to grab the dumbbell and lift it over your head until both arms are fully extended.', 'The resistance should be resting in the palms of your hands with your thumbs around it. The palm of the hands should be facing up towards the ceiling. This will be your starting position.', 'Keeping your upper arms close to your head with elbows in and perpendicular to the floor, lower the resistance in a semicircular motion behind your head until your forearms touch your biceps. Tip: The upper arms should remain stationary and only the forearms should move. Breathe in as you perform this step.', 'Go back to the starting position by using the triceps to raise the dumbbell. Breathe out as you perform this step.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Standing Dumbbell Upright Row', 'Traps', 'Dumbbell', 'Traps', array['Biceps', 'Shoulders'], array['Grasp a dumbbell in each hand with a pronated (palms forward) grip that is slightly less than shoulder width. The dumbbells should be resting on top of your thighs. Your arms should be extended with a slight bend at the elbows and your back should be straight. This will be your starting position.', 'Use your side shoulders to lift the dumbbells as you exhale. The dumbbells should be close to the body as you move it up and the elbows should drive the motion. Continue to lift them until they nearly touch your chin. Tip: Your elbows should drive the motion. As you lift the dumbbells, your elbows should always be higher than your forearms. Also, keep your torso stationary and pause for a second at the top of the movement.', 'Lower the dumbbells back down slowly to the starting position. Inhale as you perform this portion of the movement.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Standing Leg Curl', 'Hamstrings', 'Machine', 'Hamstrings', array[]::text[], array['Adjust the machine lever to fit your height and lie with your torso bent at the waist facing forward around 30-45 degrees (since an angled position is more favorable for hamstrings recruitment) with the pad of the lever on the back of your right leg (just a few inches under the calves) and the front of the right leg on top of the machine pad.', 'Keeping the torso bent forward, ensure your leg is fully stretched and grab the side handles of the machine. Position your toes straight. This will be your starting position.', 'As you exhale, curl your right leg up as far as possible without lifting the upper leg from the pad. Once you hit the fully contracted position, hold it for a second.', 'As you inhale, bring the legs back to the initial position. Repeat for the recommended amount of repetitions.', 'Perform the same exercise now for the left leg.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Standing Low-Pulley Deltoid Raise', 'Shoulders', 'Cable', 'Shoulders', array['Forearms'], array['Start by standing to the right side of a low pulley row. Use your left hand to come across the body and grab a single handle attached to the low pulley with a pronated grip (palms facing down). Rest your arm in front of you. Your right hand should grab the machine for better support and balance.', 'Make sure that your back is erect and your feet are shoulder width apart from each other. This is the starting position.', 'Begin to use the left hand and come across your body out until it is elevated to shoulder height while exhaling.', 'Feel the contraction at the top for a second and begin to slowly lower the handle back down to the original starting position while inhaling.', 'Repeat for the recommended amount of repetitions.', 'Switch arms and repeat the exercise.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Standing Military Press', 'Shoulders', 'Barbell', 'Shoulders', array['Triceps'], array['Start by placing a barbell that is about chest high on a squat rack. Once you have selected the weights, grab the barbell using a pronated (palms facing forward) grip. Make sure to grip the bar wider than shoulder width apart from each other.', 'Slightly bend the knees and place the barbell on your collar bone. Lift the barbell up keeping it lying on your chest. Take a step back and position your feet shoulder width apart from each other.', 'Once you pick up the barbell with the correct grip length, lift the bar up over your head by locking your arms. Hold at about shoulder level and slightly in front of your head. This is your starting position.', 'Lower the bar down to the collarbone slowly as you inhale.', 'Lift the bar back up to the starting position as you exhale.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Standing One-Arm Cable Curl', 'Biceps', 'Cable', 'Biceps', array[]::text[], array['Start out by grabbing single handle next to the low pulley machine. Make sure you are far enough from the machine so that your arm is supporting the weight.', 'Make sure that your upper arm is stationary, perpendicular to the floor with elbows in and palms facing forward. Your non lifting arm should be grabbing your waist. This will allow you to keep your balance.', 'Slowly begin to curl the single handle upwards while keeping the upper arm stationary until your forearm touches your bicep while exhaling. Tip: Only the forearm should move.', 'Hold the contraction position as you squeeze the bicep and then lower the single handle back down to the starting position as you inhale.', 'Repeat for the recommended amount of repetitions.', 'Switch arms while performing this exercise.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Standing One-Arm Dumbbell Triceps Extension', 'Triceps', 'Dumbbell', 'Triceps', array['Chest', 'Shoulders'], array['To begin, stand up with a dumbbell held in one hand. Your feet should be about shoulder width apart from each other. Now fully extend the arm with the dumbbell over your head. Tip: The small finger of your hand should be facing the ceiling and the palm of your hand should be facing forward. The dumbbell should be above your head.', 'This will be your starting position.', 'Keeping your upper arm close to your head (elbows in) and perpendicular to the floor, lower the resistance in a semicircular motion behind your head until your forearm touch your bicep. Tip: The upper arm should remain stationary and only the forearm should move. Breathe in as you perform this step.', 'Go back to the starting position by using the triceps to raise the dumbbell. Breathe out as you perform this step.', 'Repeat for the recommended amount of repetitions.', 'Switch arms and repeat the exercise.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Standing Overhead Barbell Triceps Extension', 'Triceps', 'Barbell', 'Triceps', array['Shoulders'], array['To begin, stand up holding a barbell or e-z bar using a pronated grip (palms facing forward) with your hands closer than shoulder width apart from each other. Your feet should be about shoulder width apart.', 'Now elevate the barbell above your head until your arms are fully extended. Keep your elbows in. This will be your starting position.', 'Keeping your upper arms close to your head and elbows in, perpendicular to the floor, lower the resistance in a semicircular motion behind your head until your forearms touch your biceps. Tip: The upper arms should remain stationary and only the forearms should move. Breathe in as you perform this step.', 'Go back to the starting position by using the triceps to raise the barbell. Breathe out as you perform this step.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Standing Palms-Up Barbell Behind The Back Wrist Curl', 'Forearms', 'Barbell', 'Forearms', array[]::text[], array['Start by standing straight and holding a barbell behind your glutes at arm''s length while using a pronated grip (palms will be facing back away from the glutes) and having your hands shoulder width apart from each other.', 'You should be looking straight forward while your feet are shoulder width apart from each other. This is the starting position.', 'While exhaling, slowly elevate the barbell up by curling your wrist in a semi-circular motion towards the ceiling. Note: Your wrist should be the only body part moving for this exercise.', 'Hold the contraction for a second and lower the barbell back down to the starting position while inhaling.', 'Repeat for the recommended amount of repetitions.', 'When finished, lower the barbell down to the squat rack or the floor by bending the knees. Tip: It is easiest to either pick it up from a squat rack or have a partner hand it to you.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Stiff-Legged Barbell Deadlift', 'Hamstrings', 'Barbell', 'Hamstrings', array['Glutes', 'Lower back'], array['Grasp a bar using an overhand grip (palms facing down). You may need some wrist wraps if using a significant amount of weight.', 'Stand with your torso straight and your legs spaced using a shoulder width or narrower stance. The knees should be slightly bent. This is your starting position.', 'Keeping the knees stationary, lower the barbell to over the top of your feet by bending at the hips while keeping your back straight. Keep moving forward as if you were going to pick something from the floor until you feel a stretch on the hamstrings. Inhale as you perform this movement.', 'Start bringing your torso up straight again by extending your hips until you are back at the starting position. Exhale as you perform this movement.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Stiff-Legged Dumbbell Deadlift', 'Hamstrings', 'Dumbbell', 'Hamstrings', array['Glutes', 'Lower back'], array['Grasp a couple of dumbbells holding them by your side at arm''s length.', 'Stand with your torso straight and your legs spaced using a shoulder width or narrower stance. The knees should be slightly bent. This is your starting position.', 'Keeping the knees stationary, lower the dumbbells to over the top of your feet by bending at the waist while keeping your back straight. Keep moving forward as if you were going to pick something from the floor until you feel a stretch on the hamstrings. Exhale as you perform this movement', 'Start bringing your torso up straight again by extending your hips and waist until you are back at the starting position. Inhale as you perform this movement.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Stiff Leg Barbell Good Morning', 'Lower back', 'Barbell', 'Lower back', array['Glutes', 'Hamstrings'], array['This exercise is best performed inside a squat rack for safety purposes. To begin, first set the bar on a rack that best matches your height. Once the correct height is chosen and the bar is loaded, step under the bar and place the back of your shoulders (slightly below the neck) across it.', 'Hold on to the bar using both arms at each side and lift it off the rack by first pushing with your legs and at the same time straightening your torso.', 'Step away from the rack and position your legs using a shoulder width medium stance. Keep your head up at all times as looking down will get you off balance and also maintain a straight back. This will be your starting position.', 'Keeping your legs stationary, move your torso forward by bending at the hips while inhaling. Lower your torso until it is parallel with the floor.', 'Begin to raise the bar as you exhale by elevating your torso back to the starting position.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Straight-Arm Dumbbell Pullover', 'Chest', 'Dumbbell', 'Chest', array['Lats', 'Shoulders', 'Triceps'], array['Place a dumbbell standing up on a flat bench.', 'Ensuring that the dumbbell stays securely placed at the top of the bench, lie perpendicular to the bench (torso across it as in forming a cross) with only your shoulders lying on the surface. Hips should be below the bench and legs bent with feet firmly on the floor. The head will be off the bench as well.', 'Grasp the dumbbell with both hands and hold it straight over your chest at arms length. Both palms should be pressing against the underside one of the sides of the dumbbell. This will be your starting position.
Caution: Always ensure that the dumbbell used for this exercise is secure. Using a dumbbell with loose plates can result in the dumbbell falling apart and falling on your face.', 'While keeping your arms straight, lower the weight slowly in an arc behind your head while breathing in until you feel a stretch on the chest.', 'At that point, bring the dumbbell back to the starting position using the arc through which the weight was lowered and exhale as you perform this movement.', 'Hold the weight on the initial position for a second and repeat the motion for the prescribed number of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Straight-Arm Pulldown', 'Lats', 'Cable', 'Lats', array[]::text[], array['You will start by grabbing the wide bar from the top pulley of a pulldown machine and using a wider than shoulder-width pronated (palms down) grip. Step backwards two feet or so.', 'Bend your torso forward at the waist by around 30-degrees with your arms fully extended in front of you and a slight bend at the elbows. If your arms are not fully extended then you need to step a bit more backwards until they are. Once your arms are fully extended and your torso is slightly bent at the waist, tighten the lats and then you are ready to begin.', 'While keeping the arms straight, pull the bar down by contracting the lats until your hands are next to the side of the thighs. Breathe out as you perform this step.', 'While keeping the arms straight, go back to the starting position while breathing in.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Sumo Deadlift', 'Hamstrings', 'Barbell', 'Hamstrings', array['Adductors', 'Forearms', 'Glutes', 'Lower back', 'Middle back', 'Quadriceps', 'Traps'], array['Begin with a bar loaded on the ground. Approach the bar so that the bar intersects the middle of the feet. The feet should be set very wide, near the collars. Bend at the hips to grip the bar. The arms should be directly below the shoulders, inside the legs, and you can use a pronated grip, a mixed grip, or hook grip. Relax the shoulders, which in effect lengthens your arms.', 'Take a breath, and then lower your hips, looking forward with your head with your chest up. Drive through the floor, spreading your feet apart, with your weight on the back half of your feet. Extend through the hips and knees.', 'As the bar passes through the knees, lean back and drive the hips into the bar, pulling your shoulder blades together.', 'Return the weight to the ground by bending at the hips and controlling the weight on the way down.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('T-Bar Row with Handle', 'Middle back', 'Barbell', 'Middle back', array['Biceps', 'Lats'], array['Position a bar into a landmine or in a corner to keep it from moving. Load an appropriate weight onto your end.', 'Stand over the bar, and position a Double D row handle around the bar next to the collar. Using your hips and legs, rise to a standing position.', 'Assume a wide stance with your hips back and your chest up. Your arms should be extended. This will be your starting position.', 'Pull the weight to your upper abdomen by retracting the shoulder blades and flexing the elbows. Do not jerk the weight or cheat during the movement.', 'After a brief pause, return to the starting position.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Thigh Abductor', 'Abductors', 'Machine', 'Abductors', array['Glutes'], array['To begin, sit down on the abductor machine and select a weight you are comfortable with. When your legs are positioned properly, grip the handles on each side. Your entire upper body (from the waist up) should be stationary. This is the starting position.', 'Slowly press against the machine with your legs to move them away from each other while exhaling.', 'Feel the contraction for a second and begin to move your legs back to the starting position while breathing in. Note: Remember to keep your upper body stationary to prevent any injuries from occurring.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Thigh Adductor', 'Adductors', 'Machine', 'Adductors', array['Glutes', 'Hamstrings'], array['To begin, sit down on the adductor machine and select a weight you are comfortable with. When your legs are positioned properly on the leg pads of the machine, grip the handles on each side. Your entire upper body (from the waist up) should be stationary. This is the starting position.', 'Slowly press against the machine with your legs to move them towards each other while exhaling.', 'Feel the contraction for a second and begin to move your legs back to the starting position while breathing in. Note: Remember to keep your upper body stationary and avoid fast jerking motions in order to prevent any injuries from occurring.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Trap Bar Deadlift', 'Quadriceps', 'Other', 'Quadriceps', array['Glutes', 'Hamstrings'], array['For this exercise load a trap bar, also known as a hex bar, to an appropriate weight resting on the ground. Stand in the center of the apparatus and grasp both handles.', 'Lower your hips, look forward with your head and keep your chest up.', 'Begin the movement by driving through the heels and extend your hips and knees. Avoid rounding your back at all times.', 'At the completion of the movement, lower the weight back to the ground under control.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Tricep Dumbbell Kickback', 'Triceps', 'Dumbbell', 'Triceps', array[]::text[], array['Start with a dumbbell in each hand and your palms facing your torso. Keep your back straight with a slight bend in the knees and bend forward at the waist. Your torso should be almost parallel to the floor. Make sure to keep your head up. Your upper arms should be close to your torso and parallel to the floor. Your forearms should be pointed towards the floor as you hold the weights. There should be a 90-degree angle formed between your forearm and upper arm. This is your starting position.', 'Now, while keeping your upper arms stationary, exhale and use your triceps to lift the weights until the arm is fully extended. Focus on moving the forearm.', 'After a brief pause at the top contraction, inhale and slowly lower the dumbbells back down to the starting position.', 'Repeat the movement for the prescribed amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Triceps Overhead Extension with Rope', 'Triceps', 'Cable', 'Triceps', array[]::text[], array['Attach a rope to a low pulley. After selecting an appropriate weight, grasp the rope with both hands and face away from the cable.', 'Position your hands behind your head with your elbows point straight up. Your elbows should start out flexed, and you can stagger your stance and lean gently away from the machine to create greater stability. This will be your starting position.', 'To perform the movement, extend through the elbow while keeping the upper arm in position, raising your hands above your head.', 'Squeeze your triceps at the top of the movement, and slowly lower the weight back to the start position.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Triceps Pushdown', 'Triceps', 'Cable', 'Triceps', array[]::text[], array['Attach a straight or angled bar to a high pulley and grab with an overhand grip (palms facing down) at shoulder width.', 'Standing upright with the torso straight and a very small inclination forward, bring the upper arms close to your body and perpendicular to the floor. The forearms should be pointing up towards the pulley as they hold the bar. This is your starting position.', 'Using the triceps, bring the bar down until it touches the front of your thighs and the arms are fully extended perpendicular to the floor. The upper arms should always remain stationary next to your torso and only the forearms should move. Exhale as you perform this movement.', 'After a second hold at the contracted position, bring the bar slowly up to the starting point. Breathe in as you perform this step.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Dumbbell Preacher Curl', 'Biceps', 'Dumbbell', 'Biceps', array[]::text[], array['Grab a dumbbell with each arm and place the upper arms on top of the preacher bench or the incline bench. The dumbbell should be held at shoulder length. This will be your starting position.', 'As you breathe in, slowly lower the dumbbells until your upper arm is extended and the biceps is fully stretched.', 'As you exhale, use the biceps to curl the weights up until your biceps is fully contracted and the dumbbells are at shoulder height.', 'Squeeze the biceps hard for a second at the contracted position and repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Underhand Cable Pulldowns', 'Lats', 'Cable', 'Lats', array['Biceps', 'Middle back', 'Shoulders'], array['Sit down on a pull-down machine with a wide bar attached to the top pulley. Adjust the knee pad of the machine to fit your height. These pads will prevent your body from being raised by the resistance attached to the bar.', 'Grab the pull-down bar with the palms facing your torso (a supinated grip). Make sure that the hands are placed closer than the shoulder width.', 'As you have both arms extended in front of you holding the bar at the chosen grip width, bring your torso back around 30 degrees or so while creating a curvature on your lower back and sticking your chest out. This is your starting position.', 'As you breathe out, pull the bar down until it touches your upper chest by drawing the shoulders and the upper arms down and back. Tip: Concentrate on squeezing the back muscles once you reach the fully contracted position and keep the elbows close to your body. The upper torso should remain stationary as your bring the bar to you and only the arms should move. The forearms should do no other work other than hold the bar.', 'After a second on the contracted position, while breathing in, slowly bring the bar back to the starting position when your arms are fully extended and the lats are fully stretched.', 'Repeat this motion for the prescribed amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Upright Barbell Row', 'Shoulders', 'Barbell', 'Shoulders', array['Traps'], array['Grasp a barbell with an overhand grip that is slightly less than shoulder width. The bar should be resting on the top of your thighs with your arms extended and a slight bend in your elbows. Your back should also be straight. This will be your starting position.', 'Now exhale and use the sides of your shoulders to lift the bar, raising your elbows up and to the side. Keep the bar close to your body as you raise it. Continue to lift the bar until it nearly touches your chin. Tip: Your elbows should drive the motion, and should always be higher than your forearms. Remember to keep your torso stationary and pause for a second at the top of the movement.', 'Lower the bar back down slowly to the starting position. Inhale as you perform this portion of the movement.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Upright Cable Row', 'Traps', 'Cable', 'Traps', array['Shoulders'], array['Grasp a straight bar cable attachment that is attached to a low pulley with a pronated (palms facing your thighs) grip that is slightly less than shoulder width. The bar should be resting on top of your thighs. Your arms should be extended with a slight bend at the elbows and your back should be straight. This will be your starting position.', 'Use your side shoulders to lift the cable bar as you exhale. The bar should be close to the body as you move it up. Continue to lift it until it nearly touches your chin. Tip: Your elbows should drive the motion. As you lift the bar, your elbows should always be higher than your forearms. Also, keep your torso stationary and pause for a second at the top of the movement.', 'Lower the bar back down slowly to the starting position. Inhale as you perform this portion of the movement.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Weighted Bench Dip', 'Triceps', 'Other', 'Triceps', array['Chest', 'Shoulders'], array['For this exercise you will need to place a bench behind your back and another one in front of you. With the benches perpendicular to your body, hold on to one bench on its edge with the hands close to your body, separated at shoulder width. Your arms should be fully extended.', 'The legs will be extended forward on top of the other bench. Your legs should be parallel to the floor while your torso is to be perpendicular to the floor. Have your partner place the dumbbell on your lap. Note: This exercise is best performed with a partner as placing the weight on your lap can be challenging and cause injury without assistance. This will be your starting position.', 'Slowly lower your body as you inhale by bending at the elbows until you lower yourself far enough to where there is an angle slightly smaller than 90 degrees between the upper arm and the forearm. Tip: Keep the elbows as close as possible throughout the movement. Forearms should always be pointing down.', 'Using your triceps to bring your torso up again, lift yourself back to the starting position while exhaling.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Weighted Crunches', 'Abdominals', 'Other', 'Abdominals', array[]::text[], array['Lie flat on your back with your feet flat on the ground or resting on a bench with your knees bent at a 90 degree angle.', 'Hold a weight to your chest, or you may hold it extended above your torso. This will be your starting position.', 'Now, exhale and slowly begin to roll your shoulders off the floor. Your shoulders should come up off the floor about 4 inches while your lower back remains on the floor.', 'At the top of the movement, flex your abdominals and hold for a brief pause.', 'Then inhale and slowly lower yourself back down to the starting position.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Weighted Pull Ups', 'Lats', 'Other', 'Lats', array['Biceps', 'Middle back'], array['Attach a weight to a dip belt and secure it around your waist. Grab the pull-up bar with the palms of your hands facing forward. For a medium grip, your hands should be spaced at shoulder width. Both arms should be extended in front of you holding the bar at the chosen grip.', 'You''ll want to bring your torso back about 30 degrees while creating a curvature in your lower back and sticking your chest out. This will be your starting position.', 'Now, exhale and pull your torso up until your head is above your hands. Concentrate on squeezing yourshoulder blades back and down as you reach the top contracted position.', 'After a brief moment at the top contracted position, inhale and slowly lower your torso back to the starting position with your arms extended and your lats fully stretched.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Wide-Grip Barbell Bench Press', 'Chest', 'Barbell', 'Chest', array['Shoulders', 'Triceps'], array['Lie back on a flat bench with feet firm on the floor. Using a wide, pronated (palms forward) grip that is around 3 inches away from shoulder width (for each hand), lift the bar from the rack and hold it straight over you with your arms locked. The bar will be perpendicular to the torso and the floor. This will be your starting position.', 'As you breathe in, come down slowly until you feel the bar on your middle chest.', 'After a second pause, bring the bar back to the starting position as you breathe out and push the bar using your chest muscles. Lock your arms and squeeze your chest in the contracted position, hold for a second and then start coming down slowly again. Tip: It should take at least twice as long to go down than to come up.', 'Repeat the movement for the prescribed amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Wide-Grip Decline Barbell Bench Press', 'Chest', 'Barbell', 'Chest', array['Shoulders', 'Triceps'], array['Lie back on a decline bench with the feet securely locked at the front of the bench. Using a wide, pronated (palms forward) grip that is around 3 inches away from shoulder width (for each hand), lift the bar from the rack and hold it straight over you with your arms locked. The bar will be perpendicular to the torso and the floor. This will be your starting position.', 'As you breathe in, come down slowly until you feel the bar on your lower chest.', 'After a second pause, bring the bar back to the starting position as you breathe out and push the bar using your chest muscles. Lock your arms and squeeze your chest in the contracted position, hold for a second and then start coming down slowly again. Tip: It should take at least twice as long to go down than to come up.', 'Repeat the movement for the prescribed amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Wide-Grip Lat Pulldown', 'Lats', 'Cable', 'Lats', array['Biceps', 'Middle back', 'Shoulders'], array['Sit down on a pull-down machine with a wide bar attached to the top pulley. Make sure that you adjust the knee pad of the machine to fit your height. These pads will prevent your body from being raised by the resistance attached to the bar.', 'Grab the bar with the palms facing forward using the prescribed grip. Note on grips: For a wide grip, your hands need to be spaced out at a distance wider than shoulder width. For a medium grip, your hands need to be spaced out at a distance equal to your shoulder width and for a close grip at a distance smaller than your shoulder width.', 'As you have both arms extended in front of you holding the bar at the chosen grip width, bring your torso back around 30 degrees or so while creating a curvature on your lower back and sticking your chest out. This is your starting position.', 'As you breathe out, bring the bar down until it touches your upper chest by drawing the shoulders and the upper arms down and back. Tip: Concentrate on squeezing the back muscles once you reach the full contracted position. The upper torso should remain stationary and only the arms should move. The forearms should do no other work except for holding the bar; therefore do not try to pull down the bar using the forearms.', 'After a second at the contracted position squeezing your shoulder blades together, slowly raise the bar back to the starting position when your arms are fully extended and the lats are fully stretched. Inhale during this portion of the movement.', 'Repeat this motion for the prescribed amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Wide-Grip Standing Barbell Curl', 'Biceps', 'Barbell', 'Biceps', array[]::text[], array['Stand up with your torso upright while holding a barbell at the wide outer handle. The palm of your hands should be facing forward. The elbows should be close to the torso. This will be your starting position.', 'While holding the upper arms stationary, curl the weights forward while contracting the biceps as you breathe out. Tip: Only the forearms should move.', 'Continue the movement until your biceps are fully contracted and the bar is at shoulder level. Hold the contracted position for a second and squeeze the biceps hard.', 'Slowly begin to bring the bar back to starting position as your breathe in.', 'Repeat for the recommended amount of repetitions.']);
INSERT INTO exercise (name, body_part, equipment, target, secondary_muscles, instructions)
VALUES ('Zottman Curl', 'Biceps', 'Dumbbell', 'Biceps', array['Forearms'], array['Stand up with your torso upright and a dumbbell in each hand being held at arms length. The elbows should be close to the torso.', 'Make sure the palms of the hands are facing each other. This will be your starting position.', 'While holding the upper arm stationary, curl the weights while contracting the biceps as you breathe out. Only the forearms should move. Your wrist should rotate so that you have a supinated (palms up) grip. Continue the movement until your biceps are fully contracted and the dumbbells are at shoulder level.', 'Hold the contracted position for a second as you squeeze the biceps.', 'Now during the contracted position, rotate your wrist until you now have a pronated (palms facing down) grip with the thumb at a higher position than the pinky.', 'Slowly begin to bring the dumbbells back down using the pronated grip.', 'As the dumbbells close your thighs, start rotating the wrist so that you go back to a neutral (palms facing your body) grip.', 'Repeat for the recommended amount of repetitions.']);
